style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
   body.user-style-soyXume{
       background-image:none;
       background-color:#CCC
   }
   .module.wtf-module.js-wtf-module.has-content{
       display:none
   }
   .enhanced-mini-profile .mini-profile .profile-summary img.avatar{
       position:relative;
       float:left;
       left:15px;
       top:-10px
   }
   .enhanced-mini-profile .mini-profile .profile-summary .fullname, .enhanced-mini-profile .mini-profile .profile-summary .screen-name{
       text-align:left;
       margin-left:80px;
   }
   .enhanced-mini-profile .mini-profile .profile-summary .fullname{
       margin-top:10px;
       margin-bottom:5px
   }
   .content-header .header-inner, .content-no-header .no-header-inner{
       display:none
   }
   .js-stream-item.stream-item.stream-item.expanding-stream-item{
       margin-bottom:10px;
       border-radius:3px
   }  
   .new-tweets-bar.js-new-tweets-bar{
       margin-bottom:10px
   }
   .nav > li > a .text{
       display:none
   }
   .Icon.Icon--bird.bird-topbar-etched{
       position:absolute;
       top:-10000px
   }
    .global-nav [role="navigation"], .nav, .nav > li{
        float:none
    }
    #global-actions > li{
        display:inline-block
    }
    .pull-right{
        position:absolute;
        width:500px
    }
    .nav li{
        display:inline-block
    }
    .global-nav .search-input{
        width:102px;
    }
    .global-nav .form-search{
        z-index:10000000000000
    }
    .Icon.Icon--cog.Icon--large{
        position:relative;
        top:-5px
    }
    .typeahead .dropdown-caret{
        left:140px
    }
    .pushstate-spinner{
        float:left
    }
}
