style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("publico.es") {    
    #header,
    #sidebar,    
    #publicidadTop,
    #toolbar,
    #adsensePreCuerpo,
    #robaAdconion,
    #publitop,
    [class^=publicidad],    
    [id^=google_ads_],    
    [id^=ligatusframe],    
    [id^=publi-],
    [id$=RobaPortada]{
        display:none;
    }
    
    #main,
    #container,
    #content,
    #content h1,
    #content h2{
        width:auto !important;
    }
    
    #main {
        padding-left: 5%;
        padding-right: 5%;
    }
    
    #content{
        background: none !important;
    }
    #container{        
        padding: 0;
    }
    
    .aperturaCont{
        text-align:center;
    }
}
