style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org")
{

/* center the page */

    html
    {
        background-color: #404040 !important;
    }
    :not(html)
    {
        background-repeat: repeat;
background-image: url(data:image/jpeg;base64,);        
    }
    
    pre.prettyprint
    {
        width: 95% !important;
    }
    .stub
    {
        font-weight: bold !important;
        text-decoration: line-through !important;
    }
    #nav-links, .stub, span.nameBlock, .useremail:not(:hover) .name:not(.capcode), .useremail:hover .name:not(.capcode), .nameBlock.capcodeMod span.name, .nameBlock.capcodeDeveloper span.name, span.capcodeMod strong.capcode, span.capcodeDeveloper strong.capcode, .postertrip, .name, .subject, .summary, .file-info, .replylink, .dateTime, .postNum
    {
        font-family: "Times New Roman", "Serif" !important;
        color: #606060 !important;
    }
    .useremail span.name:not(:hover)
    {
        text-decoration: underline !important;
    }
    .nameBlock.capcodeMod span.name, .nameBlock.capcodeDeveloper span.name, span.capcodeMod strong.capcode, span.capcodeDeveloper strong.capcode
    {
        text-decoration: underline overline !important;
    }
    .post .reply
    {
        width:100% !important;
        display: block !important;
        /*overflow: auto !important;*/
    }
    
    .opContainer
    {
        border-top-style: dashed !important;
        border-width: 2px !important;
        border-color: #c0c0c0 !important;
    }

    .thread .postContainer
    {
        float: left !important;
        width: 50% !important;
        min-width: 200px !important;
        clear: none !important;
        text-align: justify !important;
        overflow: auto !important;
        /*word-break: keep-all !important;*/
        word-wrap: break-word !important;
    }
    
    .desktop *, .globalMessage *
    {
        font-family: "Times New Roman", "Serif" !important;
        color: #606060 !important;
    }
    .center
    {
        clear:both !important;
    }
    
    hr
    {
        display: none !important;
    }
    .board hr
    {
        width: 50% !important;
        position: absolute;
        display: none !important;
    }
    
    .postMessage
    {
        margin: 0px 0px 10px 10px !important;
        color: #606060 !important;
    }
    body
    {
        width: 80% !important;
        margin-left: auto !important;
        margin-right: auto !important;
        /*background:#EEF2FF url(http://static.4chan.org/image/fade-blue.png) top center repeat-x !important;*/
    }
    
    a.replylink:not(:hover)
    {
        color: #606060 !important;
    }

/*[[grayscale-images]]*/

    .reply:target
    {
        background: #dddddd !important;
    }
    
    #nav-links:before
    {
        content: "Search: " !important;
    }
    
    button, input, select
    {
        font-size: 80% !important;
        background: #d0d0d0 !important;
        border-style: none !important;
        color: #707070 !important;
        font-family: "Times New Roman", "Serif" !important;
    }

    div.topPageNav
    {
        display: none !important;
    }
    div.pages
    {
        padding: 0px !important;
    }
    .pagelist, .pagelist div
    {
        border-style: none !important;
        background: inherit !important;
        color: #606060 !important;
        font-family: "Times New Roman", "Serif" !important;
    }

/* light colors for headings */
    
    .postingMode
    {
        background-color: rgba(0,0,15,0.45) !important;
    }
    
    .boardBanner { color: rgba(0,0,15,0.7) !important; }

/* prettify */
  
    .summary.desktop, .post, .stub
    {
        line-height: 115% !important;
        font-family: "Times New Roman", "Serif" !important;
        font-size: 130% !important;
       /*display: block !important;*/
        clear: both !important;
    }

    .replyContainer
    {
        /*background-color: white;*/
        /*order: 1px solid silver !important;*/
        /*clear: both;*/
    }

    .replyContainer + .replyContainer { margin-top: -1px; }
    .replyContainer .post.reply { margin: 0 !important; border: none !important; }
    .replyContainer .post.reply.highlight { margin: 0 !important; border: none !important; }
    .thread { margin-bottom: 50px !important; }
    blockquote.postMessage { margin: 1em 1.8em; }

    font.unkfunc, span.quote { font-style: italic !important; color: #808080 !important; }
    span.quote { padding-left: 10px !important; }
    /*span.quote:before, span.quote:after { content: "\"" !important; }*/
    /*span.quote+br+br { margin-bottom: 1ex !important; }*/

    a, a.quotelink, .backlink a.quotelink { color: #606060 !important; }
    a:hover, a.quotelink:hover { font-style: italic !important; color: #808080 !important; }

    s { color: #707070 !important; background: #707070 !important;}
    
    .bottomad
    {
        padding-top: 50px !important;
    }

    /*hr { visibility: hidden; margin: 0; padding: 0; width: 0; height: 0; }*/

    img.title { display: none; }

/* extension buttons */

    .post.op .threadHideButton,
    .fileText .extButton,
    .postInfo .extButton,
    .sideArrows .replyHideButton,
    .hide_thread_button span,
    a.menu_button, .sideArrows
    {
        opacity: 0;
    }
    
    .post.op .threadHideButton:hover,
    .fileText .extButton:hover,
    .postInfo .extButton:hover,
    .sideArrows .replyHideButton:hover,
    .hide_thread_button span:hover,
    a.menu_button:hover, .sideArrows:hover
    {
        opacity: 1;
    }
    
    .post.op .threadHideButton,
    .hide_thread_button,
    .sideArrows
    {
        position: absolute;
        left: 50%;
        margin-left: -422px !important;
        padding-top: 1px;
    }
    
}

