style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trello.com") {
    body{
        font-family: 'Droid Arabic Naskh',tahoma !important;
        font-size:85%;
    }
    .list-card-title,.card-detail-item,.action-comment p{
        direction:rtl;
    }
    .list-card-operation{
  	left:0 !important;
      	top:4px !important;
    }
    .new-comment-input,textarea.field,.js-text,.list-card-composer-textarea,.checklist-new-item-text{
        direction:rtl;
  	font-family: 'Droid Arabic Naskh',tahoma !important;
     }
.checklist-item-details-text{
   direction:rtl; 
    margin-right:25px;
  }
  .checklist-item-checkbox{
  left:initial !important;
  right:0 ;
      
    }
}
