style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("curriculum.kcdistancelearning.com") {
  /* Maximize height and width of the whole page and give it a gray background */
  html {
    height: 100%;
    width: 100%;
    background: #F5F5F5;
  }

  /* Increase font size */
  p, li {
    font-size: 1.5em;
  }

  /* Who doesn't love tables? */
  /* Expand the content area to 90%. Ah, so much better. */
  #table2 {
    width: 90%;
  }

  /* "Navbar"-related styling */
  #table2, #table2 tbody, #table3, #table3 tbody {
    position: relative;
    float: left;
    width: auto;
    background: none;
    margin-left: 3.5%;
  }
  #table2 td, #table2 tbody td, #table3 td, #table3 tbody td {
    background: none;
  }
  #table2 .title4, #table2 tbody .title4, #table3 .title4, #table3 tbody .title4 {
    font-size: 1.8em;
    color: #262626;
    font-family: sans-serif;
    font-weight: lighter;
  }

  /* Give the navbar a nice little bottom border */
  #table3 {
    border-bottom: 2px solid #006edb;
  }

  /* Hide an unnecessary blank element */
  #table4 {
    display: none;
  }

  /* Give the navbar a nice little bottom border */
  #table5 tbody {
    border-bottom: 2px solid #006edb;
  }

  #table5, #table5 tbody {
    position: relative;
    float: right;
    width: auto;
    background: none;
    margin-right: 3.5%;
  }
  #table5 td, #table5 tbody td {
    font-size: 1.2em;
    color: #262626;
    font-family: sans-serif;
    font-weight: lighter;
  }
  #table5 td a, #table5 td a:visited, #table5 tbody td a, #table5 tbody td a:visited {
    color: #006edb;
    text-decoration: none;
  }
  #table5 .title3, #table5 tbody .title3 {
    display: none;
  }

  /* Styling for the content area */
  #table7 {
    width: 90%;
    border: none;
  }
  #table7 tbody {
    border: none;
  }
  #table7 tbody tr {
    border: none;
  }
  #table7 tbody tr td {
    border: none;
  }
  #table7 tbody tr td .title3, #table7 tbody tr td .title31 {
    color: #262626;
    font-size: 1.2em;
    font-weight: lighter;
  }
  #table7 tbody tr td .style1 {
    color: #262626;
    font-size: 2em;
    font-weight: lighter;
  }

  tr td div {
    color: #000;
    font-size: 1.3em;
  }
}
