style.css:
/* i really want this to be global */
body {
    background: none repeat scroll 0 0 #F4F4F4;
    color: #333333;
    font: 82%/1.25 open sans,arial,helvetica,sans-serif;
    margin: 0 auto;
}
.pulse-header {
    background: none repeat scroll 0 0 #78BBED;
}
.zi-rhtHeaderIn {
    background: none repeat scroll 0 0 #eee;
}
.zp-rhtContainer {
    border: 1px solid #DDDDDD;  
    box-shadow: 0 1px 0 1px #EEEEEE;
    -webkit-box-shadow: 0 1px 0 1px #EEEEEE;
}
.metroTheme .newStream h4 {
    font-family: inherit;
}
.metroTheme .zpc-streamTextContent {
    font-family: inherit;
}
.getStartedwidget h3 {
    font-family: inherit;
}
.zi-headLins ul li a {
    background: inherit;
}
.zi-headLins {
    background: inherit;
}
.metroTheme .content .boldText {
    font-family: inherit;
}
.metroTheme .zpc-streamList ul.zpc-blogComments div.commentContent {
font-family: inherit;
}
