style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tumblr.com") {
    
    #container {
        width: 955px;
    }
    
    .right_column {
        float: left;
    }
    
    .sidebar_nav {
        float: left;
        margin-left: 0;
        margin-right: 0;
    }
    
    #posts.posts {
        margin-left: 15px;
    }
    
    .post_avatar {
        left: auto;
        right: -80px;
        margin-top: 15px;
    }
    
    .post_avatar:after {
        border-right: 0px;
    }
}
