style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {
/* TAB POSITION */ 
#main-window[sizemode="maximized"] #TabsToolbar{
    padding-left:750px !important;
    margin-right:-35px !important;
    padding-top:1px !important;
    
}#main-window[sizemode="maximized"] .tabbrowser-tab:not([pinned]) {
    min-width: 0px !important;
    
}#main-window[sizemode="maximized"] .tabbrowser-tab[selected] {
    

/* ADDRESS BAR */    
}#main-window[sizemode="maximized"] #nav-bar {
    top:4px !important;
    position:fixed !important;    
    background: none !important;
    border:0px !important;
    
}#main-window[sizemode="maximized"] #urlbar-container {
    width:650px !important;

}#main-window:not([inFullscreen="true"])[sizemode="maximized"][chromemargin="0,2,2,2"] #toolbar-menubar{
    overflow:hidden !important;
    position:fixed;
    top:42px;

/* NAVIGATION BUTTONS*/    
}#navigator-toolbox #nav-bar #back-button .toolbarbutton-icon {
    padding: 4px !important;
    background-color: #f3f3f3 !important;
    
}#navigator-toolbox #nav-bar #back-button:hover .toolbarbutton-icon {
    padding: 4px !important;
    background-color: #e1e1e2 !important;
    
}#navigator-toolbox #nav-bar #back-button:active .toolbarbutton-icon {
    padding: 4px !important;
    background-color: #d7d8d8 !important;
    
}#navigator-toolbox #nav-bar #forward-button .toolbarbutton-icon{
    background-color: #f3f3f3 !important;
    
}#navigator-toolbox #nav-bar #forward-button:hover .toolbarbutton-icon{
    background-color: #e1e1e2 !important;
    
}#navigator-toolbox #nav-bar #forward-button:active .toolbarbutton-icon{
    background-color: #d7d8d8 !important;
}}
