style.css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@namespace html url("http://www.w3.org/1999/xhtml");
@-moz-document url-prefix('chrome://'),url-prefix('about:downloads') {
    #downloadsRichListBox{
        padding-top:1px !important;
    }
    #PopupAutoCompleteRichResult {
    -moz-appearance:none!important;
    background-clip:padding-box!important;
    padding:1px 0px !important;
    border-top:none !important;
    }
    .autocomplete-richlistitem,.download-state{
        background: #FFF !important;
        border: solid 1px #FFF !important;
        margin:0px 1px;
        border-radius: 0 !important;
        outline:0px solid red !important;
    }
    .autocomplete-richlistitem:hover,
    .autocomplete-richlistitem[current="true"],
    .download-state:hover{
        background: #EBF3FE !important;
        border: solid 1px #B8D6FB !important;
    }
    treechildren::-moz-tree-row(focus, selected, current),.download-state[selected="true"] {
        background:#C1DBFC !important;
        border: solid 1px #7DA2CE !important;
    }
.downloadProgress{/*下载条*/
    -moz-appearance:none!important;
    border: solid 1px #7DA2CE !important;
}
    
    
    
    
#PopupAutoCompleteRichResult > .autocomplete-richlistbox > scrollbox{
display:block !important;
}
#PopupAutoCompleteRichResult > .autocomplete-richlistbox > scrollbox > box{
width:100% !Important;
}
    
    
}
