style.css:
@-moz-document url-prefix("http://pr0gramm.com") {
.item-comments {
  position: fixed !important;
  top: 0;
  left: 0;
 
  width: 300px;
  height: 100vh;
  max-height: 100vh;
  overflow-y: auto;
  overflow-x: hidden;
}

.comments-large-rectangle {
  visibility: hidden;
}
 
.item-comments textarea.comment {
  resize: none;
}
 
div.comment-box > div.comment-box {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.1);
    padding: 0 0 0 6px;
}
}
