style.css:
@-moz-document url-prefix("http://www.inoreader.com/") {
.article_title_link {
font-weight: bold;
font-size: 20px;
direction: rtl;
text-align: right;
}

.article_tile_title {
font-size: 18px;
margin: 0 0 9px;
padding: 0 18px;
opacity: 0.6;
direction: rtl;
text-align: right;
filter: alpha(opacity=60);
}

.article_tile_content {
color: #97A0AD;
direction: rtl;
text-align: right;
}

A.bluelink:visited {
color: #222222;
font-family: B Nazanin;
}


A.bluelink:link {
color: #383838;
font-family: B Nazanin;
}

.article_content[dir="rtl"] {
padding-left: 0;
padding-right: 12px;
text-align: right;
font-family: B Nazanin;
font-size: 18px;
}
.article_content {
line-height: 1.5;
}

A:visited {
text-decoration: none;
color: inherit;
font-family: B Nazanin;
}
A:link {
text-decoration: none;
color: inherit;
font-family: B Nazanin;
}

.article_unreaded .article_tile_content {
color: #383838;
font-family: B Nazanin;
font-size: 15px;
}

.article_tile_content {
color: #97A0AD;
direction: rtl;
text-align: right;
font-family: B Nazanin;
font-size: 16px;
}
}
