style.css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url-prefix('chrome://') {
  
    /* *** tabs *** */
    /* hide Australis tab backgrounds */
    .tabbrowser-tab .tab-background {
        opacity: 0;
    }
    /* hide tab splitter */
    .tabbrowser-tab::before {
        /* opacity: 0; */
    }
    /* native tab style */
    .tabbrowser-tab {
        /* it looks good on windows/linux, on mac osx you may want to change it */
        -moz-appearance: toolbarbutton;
    }
    .tabbrowser-tab[selected=true] {
        -moz-appearance: tab;
    }
    /* hide new tab button */
    .tabs-newtab-button {
        display: none;
    }
  
    /* *** nav bar *** */
    /* no rouned-corners for input boxes */
    #nav-bar textbox {
        border-radius: 0 !important;
    }
    /* native buttons (also affects tab bar) */
