/* 去标签弧边 */

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
display: none !important;
}

/* 继续去标签弧边，以及解决标签两边弧边延伸对选择标签时的干扰 */

#TabsToolbar .tab-background-middle,
#TabsToolbar .tab-background {
margin: 0px !important;
background: none !important;
}

/* 地址栏背景颜色修改，与当前标签页颜色保持一致 */

#nav-bar {
background:#FFF !important;
}

/* 激活状态颜色 */

#TabsToolbar .tabbrowser-tab[selected] {
color: #000 !important;
background-color: #FFF !important;
}

/* 标签样式 */

.tabbrowser-tab:nth-child(4n+1) {
background:#62B76E !important;
border-radius: 15px 15px 0px 0px !important;}

.tabbrowser-tab:nth-child(4n+2) {
background:#FEEA4C !important;
border-radius: 15px 15px 0px 0px !important;}

.tabbrowser-tab:nth-child(4n+3) {
background:#65CCE1 !important;
border-radius: 15px 15px 0px 0px !important;}

.tabbrowser-tab:nth-child(4n+4) {
background:#F1A4B8 !important;
border-radius: 15px 15px 0px 0px !important;}

/* 标签和新建标签按钮得到焦点 */

#TabsToolbar .tabbrowser-tab:hover:not([selected]) {
background-color: #ECF2E8 !important;
}

/* 除去标签栏背景模糊效果 */

#TabsToolbar::after {
opacity: 0.1 !important;
}

/* 隐藏原梯形标签 */

#TabsToolbar {
background: transparent !important;
margin-bottom: 0 !important;
}

#TabsToolbar .tab-background {
margin: 0 !important;
background: transparent !important;
}

/* 标签前后的弧形 */

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
display: none !important;
}

/* 标签中间的背景 */

#TabsToolbar .tab-background-middle {
margin: -4px -2px !important;
background: transparent !important;
}

/* 地址栏背景颜色修改，与当前标签页颜色保持一致 */

#nav-bar {
background:#FFF !important;
}

/* 激活状态颜色 */

#TabsToolbar .tabbrowser-tab[selected] {
color: #000 !important;
background-color: #FFF !important;
}

/* 标签样式 */

.tabbrowser-tab:nth-child(4n+1) {
background:#62B76E !important;
background-position: 0 !important;
background-size: auto !important;
border-bottom: none !important;
border-radius: 0 !important;
transform: skewX(-40deg) !important;
}

.tabbrowser-tab:nth-child(4n+2) {
background:#FEEA4C !important;
background-position: 0 !important;
background-size: auto !important;
border-bottom: none !important;
border-radius: 0 !important;
transform: skewX(-40deg) !important;
}

.tabbrowser-tab:nth-child(4n+3) {
background:#65CCE1 !important;
background-position: 0 !important;
background-size: auto !important;
border-bottom: none !important;
border-radius: 0 !important;
transform: skewX(-40deg) !important;
}

.tabbrowser-tab:nth-child(4n+4) {
background:#F1A4B8 !important;
background-position: 0 !important;
background-size: auto !important;
border-bottom: none !important;
border-radius: 0 !important;
transform: skewX(-40deg) !important;
}

.tabs-newtab-button {
