style.css:
@-moz-document domain("userstyles.org") {


/* Basic styling */

html, body, #main-header {
    font-size: 14px;
	line-height: 1.428571429;
  	background-color: #F6F6F6;
}

a, a:visited, a:active {
  color: #428bca;
  text-decoration: none;
}
a:hover {
  color: #09A275;
}
a:visited:hover {
  color: #E57D04;
}
a:active:hover {
  color: #DC0030;
}

h2, section > h2 {
  margin-left: 3px;
}

/* panel */

#left-sidebar > *,#style-info,  #additional-info, #discussions-area, #main-article table {
  border: 1px solid #e0e0e0;
  padding: 5px;
  margin-bottom: 10px;
}
/* White panel */
#style-info, table.author-styles {
  background-color: #FFFFFF;
}


/* Grey panel */

#left-sidebar > *, #additional-info, #discussions-area, #main-article table tbody {
  background-color: #f1f1f1;
  color: #555;
}
#main-article table tbody tr:hover {
  background-color: #E2E2E2;
}


/* Search */

#search-terms, #search-submit {
  margin-bottom: 8px !important;
}


/* Screenshot */

#screenshots {
  border: 0px;
}
#main-screenshot {
  overflow: hidden;
}
#main-screenshot img {
  width: 100%;
  height: auto;
  border: 0px;
}


/* Rounded */

#left-sidebar > *, #style-info, #main-screenshot img {
  -webkit-border-radius: 3px;
  -moz-border-radius: 3px;
  border-radius: 3px;
}
  
/* Install button * /

.install-status.install {
  width: 28px !important;
  height: 28px !important;
  overflow: hidden;
  white-space: nowrap;
}
.install-status.install:hover {
  width: auto !important;
}
.install-symbol {
  margin: 10px 8px;
} */
}
