style.css:
@-moz-document url-prefix("http://forums.mkboards.com/"), url-prefix("http://www.forums.mkboards.com/") {
/*[[centerlogo]]*/
/*[[roundavatars]]*/
/*[[largersigs]]*/
/*[[boxshadowfix]]*/
/*[[darkthemefix]]*/
/*[[removeusertitles]]*/
/*[[removesparkle]]*/
/*[[removeawards]]*/
/*[[removeribbons]]*/
/*[[removenamecolors]]*/
/*[[removeads]]*/
a[href="misc/style?redirect=%2Fforums%2F"].OverlayTrigger.Tooltip {
display: block !important;
}
span.img.m {
    border: none!important;
}
li.supporterHighlight,li.supporterPopupHighlight {
    display: none !important;
}
#SSD_cmfu_credit_notice {
    display: none;
}
#copyright:after {
  content: "MKBoards made better by CodyMKW | ©2014-2016 Codys Nintendo Room. All rights reserved.";
}

}

options.value:
a.avatar img,.avatar .img.s,.avatarScaler img {
    border-radius: 15px !important;
}

.avatar .img.m {
    width: 97px;
    height: 90px;
    border-radius: 15px !important;
}

.Menu .menuHeader h3 {
    text-align: center;
}

.message .signature img {
    max-height: 240px !important;
    max-width: 720px !important;
    overflow-x: auto !important;
    overflow-y: auto !important;
}

.profilePage .mast,.profilePage .mainProfileColumn,.profilePage .mast .section.infoBlock,.profilePage .mast .followBlocks .primaryContent.avatarHeap,.sectionMain,.node .nodeLastPost,.sidebar,.messageUserBlock,.thread_view .messageList .messageUserInfo a.avatar img, .thread_view .quickReply .messageUserInfo a.avatar img, .conversation_view .messageList .messageUserInfo a.avatar img, .conversation_view .quickReply .messageUserInfo a.avatar img,.avatar img, .avatar .img, .avatarCropper {
    border: 1px solid rgb(74, 74, 74) !important;
}

.profilePage .tabs.mainTabs {
    border-left: 1px solid rgb(74, 74, 74) !important;
}

.messageUserBlock .arrow {
    border-left-color: rgb(74, 74, 74);
}

.profilePage .primaryUserBlock .lastActivity,.messageList .message,.message .signature,.quickReply {
    border-top: 1px solid rgb(74,74,74) !important;
}

.primaryContent, .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo,.subForumsGrid .blockLinksGrid,.sidebar .section .primaryContent   h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3,.textHeading, .xenForm .sectionHeader {
    border-bottom: 1px solid rgb(74, 74, 74) !important;
}

.textCtrl,.textCtrl:focus, .textCtrl.Focus {
    border-top-color: rgb(74, 74, 74) !important;
    border-right-color: rgb(74, 74, 74) !important;
    border-bottom-color: rgb(74, 74, 74) !important;
    border-left-color: rgb(74, 74, 74) !important;
}

.bbCodeQuote {
    border-color: rgb(136, 136, 136);
}

.messageUserBlock .extraUserInfo img {
    display: none !important;
}

a.OverlayTrigger.Tooltip {
    display: none !important;
}

