style.css:
@-moz-document domain("youtube.com") {
.html5-play-progress, .ytp-play-progress {
   background: url("data:image/png;base64,") repeat-x !important; 
}

.html5-load-progress, .ytp-load-progress {
   background: url("data:image/png;base64,") !important;
}

.html5-scrubber-button, .ytp-scrubber-button {
   background: url("data:image/gif;base64,") !important;
   width: 50px !important;
   height: 50px !important;
   border: none !important;
   margin-left: -26px !important;
   margin-top: -21px !important;
}
.html5-progress-bar-container, .ytp-progress-bar-container {
   height: 12px !important;
}
.html5-progress-bar, .ytp-progress-bar {
   margin-top: 12px !important;
}
.html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list {
   height: 12px !important;
}

/*[[color-volume-extras]]*/

}

options.value:
/* volume control */
.ytp-volume-slider-track, .ytp-volume-slider-handle:before {
   background: /*[[color]]*/ !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, /*[[color]]*/ 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,/*[[color]]*/)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,/*[[color]]*/ 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,/*[[color]]*/ 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,/*[[color]]*/ 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,/*[[color]]*/ 100%) !important;
   z-index: -117;
}
/* extras */
.ytp-settings-button.ytp-hd-quality-badge::after, .ytp-settings-button.ytp-4k-quality-badge::after, .ytp-settings-button.ytp-5k-quality-badge::after, .ytp-settings-button.ytp-8k-quality-badge::after, .ytp-settings-button.ytp-3d-badge::after {
   background-color: /*[[color]]*/ !important;
}
.ytp-swatch-color {
   color: /*[[color]]*/ !important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
   background: /*[[color]]*/ !important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:hover:not([aria-disabled="true"]):not([disabled]) .ytp-svg-fill-logo-tube-lozenge {
   fill: /*[[color]]*/ !important;
}
