style.css:
@-moz-document url("http://gabrielecirulli.github.io/2048/") {
body, html {
	background: #776e65 !important;
	color: #faf8ef !important;
}
.game-container, .score-container, .best-container {
	background-color: #333 !important;
}
.game-won {
	background-color: rgba(187, 85, 0, 0.50) !important;
}
.tile-inner {
	background-color: #000 !important;
}
   .tile-2,    .tile-2 .tile-inner { color: #50B !important; }
   .tile-4,    .tile-4 .tile-inner { color: #00B !important; }
   .tile-8,    .tile-8 .tile-inner { color: #05B !important; }
  .tile-16,   .tile-16 .tile-inner { color: #08B !important; }
  .tile-32,   .tile-32 .tile-inner { color: #0BB !important; }
  .tile-64,   .tile-64 .tile-inner { color: #0B5 !important; }
 .tile-128,  .tile-128 .tile-inner { color: #0B0 !important; }
 .tile-256,  .tile-256 .tile-inner { color: #5B0 !important; }
 .tile-512,  .tile-512 .tile-inner { color: #8B0 !important; }
.tile-1024, .tile-1024 .tile-inner { color: #BB0 !important; }
.tile-2048, .tile-2048 .tile-inner { color: #B80 !important; }
.tile-4096, .tile-4096 .tile-inner { color: #B50 !important; }
.tile-8192, .tile-8192 .tile-inner { color: #B00 !important; }

}
