style.css:
@-moz-document domain('wykop.pl') {
	.article > div p.text { /* opis danego wykopu */
		font-size: 12px;
	}

	.comments-stream .article.preview > div:last-child { /* ogólny box z tekstem itp. danego wykopu */
		margin-left: 0px;
	}

	/* miniaturki */
	.article.preview .media-content {
		float: right;
	}

	.article.preview .media-content img {
		width: 150px;
		border: 1px solid #d8d8d8;
		transition: 0.5s;
		-webkit-transition: 0.5s;
		-moz-transition: 0.5s;
	}

	.article.preview .media-content img:hover {
		border: 1px solid #4383af;
	}
}
