style.css:
@-moz-document url-prefix("https://elaws.e-gov.go.jp/") {
/* 2020-07-16 暫定版 */

/* --- eGov 法令 Grobal --- */
 body {
    font-family: Verdana, 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', 'メイリオ', Meiryo, sans-serif;
}
a:link {
    text-decoration: none;
}
a:hover {
    text-decoration: underline;
}
}

@-moz-document url-prefix("https://elaws.e-gov.go.jp/search/elawsSearch/elaws_search/lsg0500") {
/* --- 法令本文(検索結果)ページ --- */

/* --- Groval --- */
body {
    margin:0 20px 0 20px;
    line-height: 1.5em;
}

/* --- 法令本文(検索結果)ページ ヘッダー --- */

/* --- Header 法令名 --- */
 .container-fluid table tr td .LawTitle_text {
    display: block;
    padding: 7px 15px 7px 15px;
    background-color: #F0F0F0;
    border-radius: 10px 10px 0 0;
    font-size:120%;
    line-height:1.5em;
}
/* --- Header 未反映注記 --- */
 .container-fluid table:nth-child(2) td:nth-child(2) {
    vertical-align: top;
    padding-left: 20px;
    font-size: 90%;
    line-height: 1.5em;
}
/* --- Header 法令号数・法令施行日・最終更新 --- */
 .container-fluid > table tr:nth-child(2), .container-fluid > table tr:nth-child(3), .container-fluid > table tr:nth-child(4) {
    background-color: #F9F9F9;
}
/* --- Header 法令施行日・最終更新 --- */
 .container-fluid > table tr:nth-child(3) td, .container-fluid > table tr:nth-child(4) td {
    padding-left: 0.75em;
}
/* --- Header 法令施行日 --- */
 .container-fluid > table tr:nth-child(3) td b font, .confirm_link {
    padding: 2px 4px;
    border-radius: 3px;
}
/* --- 法令目次フレーム--- */
 .container-fluid nav .ui-resizable {
    width: 20%;
}
.container-fluid nav .tab-pane {
    font-size:90%;
    line-height: 1.7em;
}
}

@-moz-document url-prefix("https://elaws.e-gov.go.jp/search/elawsSearch/elaws_search/lsg0500") {
/* --- 法令本文フレーム --- */
 .LawBody {
    line-height: 1.75em!important;
    font-size: 16px;
    font-family: Verdana, 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', 'メイリオ', Meiryo, sans-serif!important;
}
/* --- 法令タイトル --- */
 .LawNum {
    font-size:110%;
}
.LawTitle {
    display: block;
    margin: 5px 0 5px 0!important;
    padding: 5px 10px 5px 10px;
    background-color: #F0F0F0;
    border-radius: 5px;
    font-size:150%;
    line-height:1.5em;
}
.EnactStatement {
    text-indent:0;
    margin: 1em 0;
}
/* --- 目次 --- */
 .TOC {
    margin: 1.5em 0;
}
.TOCLabel {
    margin: 0 0 1.5em 0;
    font-size:120%;
    font-weight: 700;
}
.TOCChapter {
    margin: 0 0 1.5em 0;
}
.TOCChapterTitle, .TOCChapterTitleNum, .TOCChapterTitleBody, .TOCSection, .TOCSectionTitle {
    line-height: 1.0em;
}
/* --- 章 --- */
 .Chapter {
    margin: 4em 0 1em 0;
}
/* --- 章 タイトル --- */
 .ChapterTitle {
    margin-left:0!important;
    font-size:150%;
    padding-bottom: 0.5em;
    border-bottom: 1px solid #CCC;
}
/* --- 節 --- */
 .Section {
    margin: 1em 0 2em 0!important;
}
/* --- 節 タイトル --- */
 .SectionTitle {
    margin: 0.5em 0!important;
    font-size:130%;
    font-weight: 700;
}
/* --- 款 タイトル ---*/
 .SubsectionTitle {
    margin: 0.5em 0 0.5em 0.5em;
    font-size:120%;
    font-weight: 700;
}
/* --- 条 --- */
 .Article {
    margin: 1.5em 0;
}
.ArticleCaption {
    margin:0 0 1em 0!important;
    font-size:120%;
    font-weight:700;
}
.Paragraph, .ParagraphSentence {
    margin: 0 0 1.5em 2em!important;
    text-indent: -2em!important;
}
.Paragraph .TableStruct {
    margin: 1em 0 1.5em 1em;
    text-indent: -1em;
}
/* --- 条のn --- */
 .ParagraphSentence {
    margin: 10px 0 0 2em!important;
    text-indent: -2em!important;
}
/* --- 条 項 --- */
 .Item {
    margin-top: 0.75em;
}
.ItemSentence {
    margin-left:2em!important;
    text-indent: -2em!important;
}
.Subitem1 {
    margin-top: 0.75em;
}
.Subitem1Sentence {
    margin-left:2em!important;
    text-indent: -2em!important;
}
/* --- 附則 --- */
 .SupplProvision {
    margin: 3em 0 1em 0;
    padding: 10px;
    border-radius:7px;
    background-color: #F9F9F9;
}
.SupplProvisionLabel {
    margin:1em 0 1em 0.5em!important;
    font-size:140%;
    padding-bottom: 0.2em;
}
.ParagraphCaption {
    margin:1em 0 1em 0;
    font-size:120%;
}

}
