style.css:
@-moz-document url-prefix("http://www.google.com"), url-prefix("https://www.google.com") {
html {
    min-height: 100%;
}
a {
    -webkit-transition: all .2s ease-in-out;
    -moz-transition: all .2s ease-in-out;
    transition: all .2s ease-in-out;
}
#searchform {
    z-index: 9999;
}
#mngb {
    position: relative;
    z-index: 999;
}
body#gsr {
    background: #d3d3d3;
    min-height: 100%;
    height: 100%;
}
body#gsr:after {
    content: '';
    display: block;
    position: fixed;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background: #d3d3d3 url("http://wwc.instacam.com/instacamimg/ATLGM/ATLGM_l.jpg?rnd=15-120420141503") 50% 50% no-repeat !important;
    background-size: cover !important;
    -webkit-transform: rotate(.8deg) scale(1.1);
    -moz-transform: rotate(.8deg) scale(1.1);
    -o-transform: rotate(.8deg) scale(1.1);
    transform: rotate(.8deg) scale(1.1);
}
#gbqfqw {
    background: rgba(211, 211, 211, .6);
}
#lga {
    position: relative;
    z-index: 300;
}
#lga img {
    display: none;
    background: url(http://x.co/utmgif);
}
#lga:after {
    display: block;
    content: 'Google';
    font: normal 128px/200px 'Trebuchet ms', serif;
    color: #fff;
    text-shadow: 0 1px 0 #ccc, 0 2px 0 #c9c9c9, 0 3px 0 #bbb, 0 4px 0 #b9b9b9, 0 5px 0 #aaa, 0 6px 1px rgba(0, 0, 0, .1), 0 0 5px rgba(0, 0, 0, .1), 0 1px 3px rgba(0, 0, 0, .3), 0 3px 5px rgba(0, 0, 0, .2), 0 5px 10px rgba(0, 0, 0, .25), 0 10px 10px rgba(0, 0, 0, .2), 0 20px 20px rgba(0, 0, 0, .15);
    -webkit-transform: scaleY(.85);
    -moz-transform: scaleY(.85);
    transform: scaleY(.85);
}
#lga div {
    padding: 0 !important;
}
#footer {
    position: fixed !important;
}
#footer>div {
    border: 0;
    background: rgba(255, 255, 255, .6);
    height: 22px;
}
div.mw {
    background: #d3d3d3;
}
div#flci {
    display: none !important;
}
#main #footer a {
    color: #666 !important;
    text-shadow: 0 1px 1px rgba(255, 255, 255, .6) !important;
}
#main #footer a:hover {
    text-decoration: none !important;
    color: #000 !important;
    text-shadow: none !important;
}
#rcnt {
    background: #d3d3d3 !important;
}
#prm,
#hplogo,
#cpBackgroundImg {
    display: none !important;
}
#gbi4t {
    color: #d3d3d3 !important;
}
#main {
    position: relative;
    z-index: 300;
}
body.vsh #gac_scont {
    display: none !important;
    background: none !important;
    -webkit-transition: background .5s ease-in-out;
    -moz-transition: background .5s ease-in-out;
    transition: background .5s ease-in-out;
}
#cnt.big {
    background: #d3d3d3 !important;
}
#rhs_block .std,
#bottomads,
.pmoabs {
    display: none !important;
}
li.g {
    margin-bottom: 0 !important;
    padding-bottom: 23px !important;
}
#fll {
    margin: 0 auto !important;
    padding: 19px auto !important;
}
#gb#gb a.gb_a,
#gb#gb a.gb_b {
    color: #aaa;
}
#gb#gb a.gb_a:hover,
#gb#gb a.gb_b:hover {
    color: #fff;
}
}
