style.css:
@-moz-document domain("fanfare.metafilter.com") {
  #body,
  .mefimessages,
  .signout,
  #body > div.copy {
    background: #000000 !important;
    color: #FFFFFF !important;
  }

  #topline,
  #bottomline,
  .ads,
  .sidebar,
  .tags,
  #taglist,
  #footer,
  #tv,
  #body > div.copy  {
    background: #222 !important;
    color: #FFFFFF !important;
  }

  div.comments#prevDiv[style],
  div.comments#prevDiv2[style] {
    border-color: #ccc !important;
    background: #000000 !important;
  }

  input#link_title[style],
  #comment[style],
  #extended[style],
  #tags[style] {
    background: #222 !important;
    color: #FFFFFF !important;
    border: solid 1px #ccc !important;
  }

}
