style.css:
@-moz-document url-prefix("http://www.forocoches.com/") {
/* Muestro los iconos superior para navegar a la página home */

/* Muestro el banner superior */
body > div[align="center"]:nth-of-type(1),
body > div[align="center"]:nth-of-type(2)
{ display: inherit !important; }

/* Reemplazo las imágenes de los logos por otras más genéricas */
/* Logo coche */
#AutoNumber1 img[width="121"] { 
  display: block !important;
  -moz-box-sizing: border-box !important;
  box-sizing: border-box !important;
  background: url('http://reyesr.github.io/fullproof/img/home.png') no-repeat;
  width: 32px !important; /* Width of new image */
  height: 32px !important; /* Height of new image */
  padding-left: 48px !important; /* Equal to width of new image */
}

/* Logo forocoches */
#AutoNumber1 img[width="259"] { 
  display: block !important;
  -moz-box-sizing: border-box !important;
  box-sizing: border-box !important;
  background: url('http://cdn.husqvarna.com/c/h/product-sub-heading/es/articulos-para-ts-73-r-612c9324.png') no-repeat;
  width: 273px !important; /* Width of new image */
  height: 32px !important; /* Height of new image */
  padding-left: 273px !important; /* Equal to width of new image */
}

#AutoNumber1 img[usemap="#logo"] {
	opacity:1;
	filter:alpha(opacity=100)
}

#AutoNumber1 td { background: #fff; }

}
