style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cloud66.com") {
    .link-production { color: red; font-weight: bold; }
    
    .stack.production-stack .mod-article-pri-header {
        background-image: linear-gradient(#FF3019, #CF0404);    
    }
    
    .production-stack .stack-heading a, .production-stack .stack-heading span.js-stack-name {
        color: #FFF;
    }
    
    .production-stack .mod-article-pri-header h1 .lighter {
        color: #FFF;   
    }
    
    .production-stack .mod-article-pri-header h1 a .lighter {
        color: #FFF;   
    }
    
    .production-stack .mod-article-pri-header h1 a {
       color: rgb(0, 136, 204) !important;
       color: #FFF !important;
    }   
    
    .production-stack .tool-list a.dropdown-toggle {
       color: #FFF;
    }
}
