style.css:
@-moz-document url("http://www.hvilkenuke.com/aal/hvilken-uke") {
#form1 > div.veckonummer, #form1 > div.toppen, #form1 > div.kalender, #form1 > div.bottom, #form1 > h1,#form1 > div.fb-liked,#form1 > div.gplus,#form1 > div.twitter, #form1 > div.rightads, #form1 > div.leftads{
  display:none !important;
}
::selection{
  background: #F868D0;
}
/*Background-color*/
body{
  background-color: /*[[AllBackground]]*/;
}
#form1 > div.vecka{
  cursor: pointer;
  position: fixed;
  top: 40%;
  left: 50%;
  margin-top: -50px;
  margin-left: -100px;
  background: /*[[WeekIn]]*/;
  border: 6px solid rgba(255, 255, 255, 0);
  border-radius: 35px;
  -webkit-transition: background-color 0.3s;
}
#form1 > div.vecka:hover{
  background-color: /*[[WeekHover]]*/;
  border: 6px solid /*[[WeekBorder]]*/;
}
#form1 > div.fulldate{
  position: fixed;
  top: 70%;
  left: 50%;
  margin-top: -50px;
  margin-left: -100px;
  color: #FA9F16;
}
}
