style.css:
@-moz-document domain(trello.com) {
html,body,input,select,textarea,button {
	font:13px 'Tahoma',sans-serif;
	line-height:17px;
	font-weight:300;
}

.list-card a {
	color:#444;
}

.list-title .icon-menu {
	right:0;
	top:8px;
}

.list-gradient-top,.list-gradient-bottom {
	display:none;
}

.list-area-wrapper {
	padding-left:10px;
}

.list-area {
	width:3500px;
}

.list-header {
	box-shadow:0 rgba(0,0,0,0.2);
	padding:8px;
	background:#fff;
	color:#3598DB;
	z-index:10;
	margin:0;
}

.list-header h2 {
	font-size:15px;
	font-weight:400;
	text-transform:uppercase;
}
.list-card-labels .card-label {
line-height:0px;
font-weight:100;
text-align:center;
font-size:11px;
width:50px;
height:14px;
line-height:normal;
}

.list-header-menu-icon {
	top:5px;
	right:0;
}

.list {
	background:rgba(252,252,252,.9);
	width:272px;
	margin:0 5px;
	padding:0;
}

.list-cards {
	padding:0px ;
}

.list.add-list .placeholder {
	display:none;
	padding:7px;
	color:#E84C3D;
}

.list-card {
    padding: 5px 2px 5px 2px;
    margin: 8px 2px 8px 2px;
    border-radius: 0;
    box-shadow: 0 1px 0 rgba(9,30,66,.10)!important;
}

.board-header-btn {
	color:#fff;
	font-weight:400;
}

/* Change the Header and Board-Canvas Background to a different color or use url to get image
Comment out the relevant sections below to use Trello Default Colors for header and background*/

/*Main Trello Header*/
#header {
	background:#2d3e50!important;
}

/*Comment Out .board-header & .board-canvas if you want to use Trello photo/color backgrounds*/
.board-header {
	height:30px;
	background:#E84C3D!important;
	padding:3px;
}

.board-canvas {
	background: #2D3E50;
	background-size:cover;
}

/*End custom header & canvas background*/

#board {
	padding:8px 0 0;
}

.list {
    border-radius: 2px;
 }


.header-user {
	top:10px;
	right:5px;
}



.open-card-composer {
	margin:0;
	padding:7px 10px;
}

h2 {
	font-size:18px;
	line-height:1.2em;
	margin:0 0 9px;
	font-weight:400;
}


.member {
	border-radius:20px;
	border:none;
}

.member img {
	border-radius:0;
	border:none;
}


.fancy-scrollbar::-webkit-scrollbar-track-piece {
	background:none;
}

.fancy-scrollbar::-webkit-scrollbar-thumb:vertical,.fancy-scrollbar::-webkit-scrollbar-thumb:horizontal {
	background:#555;
}

.open-card-composer:hover {
	background:#ECF0F3;
	color:#666;
	text-decoration:none;
	margin:0;
}

.window-wrapper {
	background:#FFF;
	border-radius:2px;
    padding-top:8px;
}

.board-widgets {
	background:rgba(255,255,255,0.98);
}

.sidebar-hide-btn {
	top:5px;
}

.atMention {
	background:#A3359F;
	border-radius:5px;
	color:#fff;
	font-size:12px;
}

code {
	font-family:Menlo,Monaco,"Courier New",monospace;
	background-color:#F7F7F9;
	border:1px solid #E1E1E8;
	border-radius:3px;
	padding:3px 4px;
	font-size:12px;
	color:#D14;
}
/*Text On Labels*/

  .body-board-view .list-card-labels .card-label
  {
    font-weight: bold;
    color: #fff ;
    text-transform: none;
	font-family: 'Tahoma',sans-serif;
    line-height: 14px ;
    height: auto ;
    padding: 2px 6px ;
    margin-right: 5px ;
    width: auto ;
    text-shadow: none ;
    font-size: 10px ;
    opacity: .8 ;
    margin-left: 0 ;
  }

  .body-board-view .list-card-labels
  {
    display: flex ;
  }
    
    .card-label {
    border-radius: 1px;
}
}
