style.css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("matariyaruo.doorblog.jp") {
    #main {
        float: left!important;
    }
    body.layout-2col #main {
        width: 850px!important;
    }
    #main-inner > .blogroll_wrapper {
        display: none!important;
    }
}
@-moz-document url-prefix("http://blog.livedoor.jp/nyusokudeyaruo/") {
    #content > iframe, .blogbody iframe, .blogbody > div:nth-child(9) {
        display: none!important;
    }
    #content > div:first-child:after {
        content: " － 非表示中";
    }
}
@-moz-document url-prefix("http://katteniyaruo.blog69.fc2.com/blog-entry-") {
    #header {
        display: none!important;
    }
    #left {
        margin-left: 50px!important;
        width: 970px!important;
    }
    body > a:nth-child(1) > img:nth-child(1){
        height: 50px;
    }
}
@-moz-document url-prefix("http://mukankei961.blog105.fc2.com/blog-entry-"), url-prefix("http://mukankei151.blog47.fc2.com/blog-entry-") {
    body > a:nth-child(3) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(5),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > table:nth-child(5),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > br:nth-child(7),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > br:nth-child(8){
        display: none!important;
    }
    h1, h1 a img,
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > table:nth-child(1),
    body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1){
        height:50px!important;
    }
    h1{
        margin-bottom: 0!important;
        margin-top: 10px!important;
    }
    body > a > table > tbody > tr > td:first-child:before {
        background: #fff;
        content: "ヘッダ部分非表示中";
        display: block!important;
    }
    td>table.Frame:nth-child(6) {
        width: 1020px!important;
    }

}
@-moz-document domain("yaruonichijou.blog.fc2.com") {
    #center {
        float: left!important;
    }
    #left {
        float: right!important;
    }
}
@-moz-document url-prefix("http://oyoguyaruo.blog72.fc2.com/") {
    #header > h1 > a > img, .menu_outline3, #yads, #zenback {
        display: none!important;
    }
    #header > h2:after {
        content: "泳ぐやる夫シアター";
        display: block!important;
    }
    #header {
        height: 50px;
    }
    .menu_outline3:first-child {
        display: block!important;
    }
}
@-moz-document url-prefix("http://oyoguyaruo.blog72.fc2.com/blog-entry") {
    td.left_outline {
        display: none!important;
    }
}
@-moz-document url-prefix("http://aamatome.blog31.fc2.com/") {
    .rss1, div.content:nth-child(7), #header > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > img:nth-child(1) {
        display: none!important;
    }
    div#header {
        height: 60px;
    }
    #main {
        float: left!important;
    }
    #sidemenu {
        float: right!important;
    }
}
@-moz-document domain("yaruyalibrary.blog.fc2.com") {
    #header > h1 > a > img {
        display: none!important;
    }
    #header > h1 > a:after {
        content: "やるやら書庫";
    }
    td#header {
        height: 50px;
    }
}
@-moz-document url-prefix("http://yaruyalibrary.blog.fc2.com/blog-entry-") {
    td.left_outline {
        display: none!important;
    }
}
@-moz-document url-prefix("http://rusalka777.blog.fc2.com/blog-entry-") {
    td.left_outline {
        display: none!important;
    }
    table.base_table {
        width: 70em;
    }
}
@-moz-document url-prefix("http://yaruomatome.blog10.fc2.com/blog-entry-") {
    html {
        top: 0;
    }
    td.left_outline {
        background: none;
    }
    #sh_fc2blogheadbar, #header_body, #center>div.plugin3_outline, #left, div.ently_outline:nth-child(2) {
        display: none!important;
    }
    #outline {
        text-align: left;
        width: 1330px;
        background: url('http://blog-imgs-24-origin.fc2.com/y/a/r/yaruomatome/br0609lv136.gif') fixed;
    }
    #outline:before {
        font-size: 1.5em;
        content: 'やる夫ブログ';
        color: #FFF;
    }
    #center {
        float: left;
        width: 1120px;
    }
    #right {
        float: left;
    }
    #center-left {
        width: 1120px;
        margin-left: 30px;
    }
    .comment_outline {
        margin-bottom: 5px;
    }
}
@-moz-document url-prefix("http://yaruyarach.blog.fc2.com/blog-entry-") {
    #index_area, #left {
        display: none!important;
    }
    #outline {
        width: 1230px;
    }
    #center {
        float: left;
        width: 1150px;
    }
    #center-left {
        width: 1150px;
        margin-left: 30px;
    }
}
@-moz-document url-prefix("http://gokumonan.blog87.fc2.com/blog-entry-") {
    #header_body {
        height: 80px;
    }
    #header_body>h1 {
        margin-top: 10px;
    }
    #left, div.c.en1 {
        display: none!important;
    }
    #outline {
        width: 1230px;
    }
    #center {
        float: left;
        width: 1150px;
    }
    #center-left {
        width: 1150px;
        margin-left: 30px;
    }
}
@-moz-document url-prefix("http://yaruokei.blog.fc2.com/blog-entry-") {
    #sh_fc2blogheadbar, #header, #container > a:nth-child(8) > span:nth-child(1), #container > a:nth-child(8) > span:nth-child(2), a[onmousedown] {
        display: none!important;
    }
}
@-moz-document url-prefix("http://ansokuwww.blog50.fc2.com/") {
.msg {
    font-family: 'ＭＳ Ｐゴシック', 'ＭＳＰゴシック', 'MSPゴシック', 'MS Pゴシック';
    font-size: 16px;
    line-height: 18px;
    font-weight:normal;
}
}
@-moz-document url-prefix("http://burakio002.blog97.fc2.com") {
    #header > h1:nth-child(1) > a:nth-child(2) > img:nth-child(1) {
        display: none!important;
    }
}
@-moz-document url-prefix("http://yymatome.blog.fc2.com/blog-entry-") {
    #header_body > h1:nth-child(1) > a:nth-child(1) > img:nth-child(1){
        height: 30px!important;
    }
    #header_body > h1:nth-child(1) > a:nth-child(1){
        height:20px;
    }
    .comment_outline {
        margin-bottom: 10px!important;
    }
}
@-moz-document url-prefix("http://copymatome.blog108.fc2.com/blog-entry-") {
    #header {
        height: 20px!important;
        background: none!important;
    }
    #header > h1:nth-child(1) > a:nth-child(1) > img:nth-child(1) {
        height: 0!important;
    }
    #header > h1:nth-child(1) > a:nth-child(1):after {
        content: "やる夫エッセンシャル";
    }
}
@-moz-document url-prefix("http://yarupon.blog134.fc2.com/blog-entry-") {
    #center {
        float: left!important;
    }
    #left{
        float: right!important;
    }
    #center-left {
        margin-left: 15px;
    }
    #outline {
        width:1700px;
    }
    iframe{
        display: none;
    }
}
@-moz-document url-prefix("http://snudge.blog38.fc2.com/blog-entry-") {
    #titleTable img {
        height:50px;
    }
}
@-moz-document url-prefix("http://yaruonichijou.blog.fc2.com/blog-entry-l") {
    #header_body h1 img {
        height : 100px;
    }
}
@-moz-document url-prefix("http://n-yaruomatome.sakura.ne.jp/blog/") {
    #contentInner{
        margin-left: -25%;
    }
    #contentInner main {
	margin-left: 20%;
    }
    #side aside{
        max-width: 250px;
    }
}
@-moz-document url-prefix("http://yanchor.blog.fc2.com/blog-entry") {
    #header_body > h1 > a > img {
        height: 50px;
    }
    #left {
	float:right;
    }
    #center {
        float: left;
    }
}
@-moz-document url-prefix("http://memocopy.blog45.fc2.com/blog-entry-") {
    .article {
        line-height:1.0;
    }
}

@-moz-document url-prefix("http://mukankei151.com/wp/archives/") {
.textar-aa {
    font-family: 'ＭＳ Ｐゴシック', 'ＭＳＰゴシック', 'MSPゴシック', 'MS Pゴシック';
    font-size: 16px;
    line-height: 18px;
    font-weight:normal;
}
}
