style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("realkana.com") {
    body {
        background-color: #333;
        color: #fff;
    }
    #all {
        left: 0%;
        margin-left: 10px;
    }
    #all.b-1 #b, #all.b-1 #bottom, #all.b-1 #front a, #all.b-1 #kana, #all.b-1 #preview, #all.b-1 #t, #all.b-1 #top, #all.b-1, #all.b-1 #tabs #front {
        background-image: none;
        background-color: #666;
    }
    #tab {
        border-color: #000;
    }
    #tabs {
        background-color: #333 !important;
        background: none;
    }
    #tabs li {
        border-color: #000;
    }
    a, a:visited, .link {
        color: #EEF
    }
    #question {
        background-color: #DDD;
        border-radius: 25px;
        width: 200px;
        margin-left: auto;
        margin-right: auto;
    }
}
