style.css:
@-moz-document domain("fusiontables.google.com") {

/* hide header */
div[style="position: absolute; overflow: hidden; left: 0px; top: 0px; right: 0px; height: 70px;"] {
	display: none
}

/* let main content sit at the top */
body > div[style^="position: absolute;"],
div[style="position: absolute; overflow: hidden; left: 0px; top: 70px; right: 0px; bottom: 0px;"] {
	position: static !important
}


/* dialog */

.gwt-DialogBox {
	left: 1em !important;
	top: 1em !important;
	padding: .5em 0 .75em .5em;
}

.gwt-DialogBox .Caption,
.ft-modal-dialog-title {margin-bottom: .5em}

.GNV4XGXBJCB {
	width: 2.25em;
	height: 2.25em;
}

.GNV4XGXBEOB {
	height: auto !important;
	padding-right: 0;
}

textarea {height: 1.23em}
}
