style.css:
@-moz-document domain("portal.victorops.com"), domain("stportal.victorops.com") {
/* Hides the shark fin on chats */
.chat .chat-bubble:before, .chat .chat-bubble:after 
{
  display: none;
}

/* reduce the space below messages */
.timeline>li, .chat 
{
  margin-bottom: 2px !important;
}

/* shrink the padding within the boxes */     
.action-suspend, .action-resume, .action-incident, .action-oncall, .chat .chat-bubble, .action-page
{
  padding: 2px 10px;
  border-radius: 2px;
}

/* shrink the input */
.timeline-input 
{
  padding: 5px;
  top: 0px;
}

/* move the filter button up so everythign is nice */
.timeline-input .filter-toggle
{
   top: 13px;
   right: 5px;
}

/* move the timeline up */
#timeline .timeline-container
{
  top: 40px; 
}

/* less right / left padding on the timeline */
.timeline 
{
  padding: 0px 5px 10px;
}

.entry-time-wrapper strong
{
  width: 100px;
  display: inline-block;
  text-align: right;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  margin-bottom: -4px;
}
}
