style.css:
@-moz-document url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/") {

::selection {background-color:transparent;color: #9a0707;}
::-moz-selection {background-color:transparent;color: #9a0707;}
::-webkit-selection {background-color:transparent;color: #9a0707;}

body {
   background:url("http://i.cubeupload.com/cvboL0.png")repeat fixed  !important;
   background-color: #333 !important;
   background-position: 0% 60% !important;
 }

div.l-header .tab.iconic a{
  background-color: #9a0707 !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Consolas; color: #555;
   background: #9a0707 !important;

 }


.notification  {
  	background: #9a0707 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #fff !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('http://i.cubeupload.com/Qkhr1Q.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: transparent !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 71px !important;
  background-repeat: no-repeat;
  background: url("http://i.cubeupload.com/twE3rU.png")no-repeat !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Posts" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Comrades" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Saved Work" !important;
}.controls_section li .activity:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px #9a0707 !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}

#right_column.right_column::after {
  background: transparent;
}
div.search_form_row {
  background-color: #790303;
  color: #000;
  content: "Search" !important;}
.search_query {
    font-family: consolas; color: #fff;}
  
#return_to_top .return_to_top_icon{
  background: url('http://i.cubeupload.com/Y1EmVh.png') no-repeat transparent!important}

#tab_switching .tab_notice, div.l-header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
background-color: #790303;
  color: #fff;
  border-radius:50px;
}

.new_post_label{ font-size: 0 !important }
.new_post_label > I:before { content: none !important }
#new_post{ background: url('http://i.cubeupload.com/Qkhr1Q.png') center no-repeat white !important }

.l-content {
background-color: #9a0707;
  font-family:consolas;
}

#tab_switching .tab_notice, .l-header .tab .tab_notice, .popover_blogs .tab_notice, .blog_menu .selected_blog .tab_notice {
display: inline;
position: absolute;
top: -8px;
right: -5px;
height: 18px;
min-width: 17px;
padding: 0 3px 0 3px;
line-height: 18px;
text-align: center;
font-weight: bold;
font-size: 12px;
color: #fff;
background-color: #9a0707;
border-radius: 50px;
background-clip: padding-box;
pointer-events: none;
-webkit-box-sizing: border-box;
-moz-box-sizing: border-box;
box-sizing: border-box;
}

.post_full .post_permalink:before {
content: '';
position: absolute;
top: -1px;
right: -1px;
border-right: 16px solid #9a0707;
border-left: 16px solid transparent;
border-bottom: 16px solid transparent;
-moz-transform: scale(.9999);
}

.plexi.show.color {
background: url('http://i.cubeupload.com/HteBBm.png');
  opacity:.75;
}
}
