style.css:
@-moz-document url-prefix('https://www.tumblr.com/dashboard'),url-prefix('https://www.tumblr.com/blog'),url-prefix('https://www.tumblr.com/tagged'),url-prefix('https://www.tumblr.com/inbox'),url-prefix('https://www.tumblr.com/likes') {

/*BACKGROUND*/
body {
background: #2B2831 url('http://i.imgur.com/3urOKpO.jpg') top repeat!important;
}

/*POST AREA BACKGROUND*/
.l-content {
background-color: rgba(43, 40, 49, .9)!important;
padding: 20px!important;
}

/*ARROW THING AT THE TOP POINTING TO THE HOSUE*/
.l-header .selection_nipple {
border:none!important;
}

/*SEARCH BACKGROUND*/
.search_query {
background: #2B2831!important;
}
 
/*SEARCH FONT*/ 
.search_query {
color:#2B2831!important;
}

/*GREYSCALE ICONS*/
.post.new_post .new_post_label,
.controls_section a .count canvas{
-webkit-filter: grayscale(100%);
-moz-filter: grayscale(100%);
filter: grayscale(100%);
}

/*RANDOM UGLY FOOTER THING IDK WHAT IT IS*/   
.right_column:after{
display:none!important;
}

/*PERMALINK CORNER*/ 
.post_full .post_permalink:before {
border-right: 16px solid #2A2730!important;  
}

/*REMOVES THE BACKGROUND ON USERNAME.TUMBLR.COM FOLLOW BUTTONS, DO NOT REMOVE*/ 
#dashboard_iframe {
background: transparent!important;
}

/*REMOVES MORE BACKGROUNDS*/ 
.l-container.l-container--two-column-dashboard .left_column {
background: transparent!important;
}
      
.l-container.l-container--two-column .right_column, .l-container.l-container--two-column-dashboard .right_column {
background: transparent!important;
}

/*NEW HEADER*/ 
.l-header-container--refresh {
box-shadow: 0 0 0 0!important;
background-color: rgba(43, 40, 49, .9)!important;  
position: absolute!important;
}

.l-header-container--refresh .l-header {
min-width: 920px!important;
max-width: 920px!important;
}

.tab_notice {
background: #D95E40!important;
}

.identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
border: 2px solid rgba(43, 40, 49, 0.9)!important;
