style.css:
@-moz-document domain(www.tumblr.com) {

  
:root {
--bg: #1d1e2a !important;          /*  var(--bg)   */
--bg2: #1a1b26 !important;          /*  var(--bg2)   */  
--bg-dark: #14151F !important;             /*  var(--bg-dark)   */
--bg3: rgba(20,21,31,0.89) !important;      /* semi transparent bg */
--first-color: #888 !important;             /*  grey  */  
--second-color: #74857c !important;             /*  green   */  
--third-color: #556b82 !important;             /* blue  */
}    
  
  
                                                  /* bg */  
body, .l-container.l-container--two-column-dashboard .l-content, .l-container.l-container--two-column .l-content, .l-container.l-container--two-column-dashboard .left_column, .post_avatar, .l-container.l-container--two-column-dashboard .right_column, .l-container.l-container--two-column .right_column {
    background-color: var(--bg) !important;
  }  
  
  
/* header */
.l-header-container--refresh, .ui_search .search_query, .post_full.is_note .post_body .note_item, .popover_header, li.popover_subsection, .popover--account-popover .popover .popover_menu_item:hover .popover_menu_item_anchor, .popover--account-popover .popover .popover_menu_item_blog.popover_menu_item_blog, .post_brick, .post_brick.is_conversation .conversation_lines .chat_line, #tabs .tab, #tabs, .chrome.blue, .grey, .white_pane, div.section:nth-child(2), .ui_notes .date_header, .date_header {
    background-color: var(--bg-dark) !important;
  }  
  
  
/* posts */
  .post.post_full, .plus-follow-button, .post .post-source-footer, .tab-bar-container .tab_notice, .blog-sub-nav-details .blog-sub-nav-item.currentItem, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item:hover, .popover_gradient > .popover_inner, .blog-list-item.blog-list-item--on-light:hover, .post_brick .expand-post-content, .post_brick.is_photo .post_media + .expand, .blog-activity .ui_notes .blog-activity-date .date_header, .blog-activity .blog-activity-more, .follower.invite_someone, input.text_field, .follower, .l-container--two-column .pagination, #cards .card, .notifications_page .section.divider, .ui_stats .unit .stat_txt, #user_graphs .axis-label, .ui_stats .unit .button {
    background-color: var(--bg2) !important;
  }  
  
  .compose-button {
    fill: var(--bg) !important;
  }  
  
  g.highcharts-grid:nth-child(3) > path, g.highcharts-grid:nth-child(4) > path {
    stroke: var(--bg2) !important;
  }
                                               /* text color */  
  
body, .identity .controls_section.user_list li .follow_list_item_blog .tumblelog_name, li.popover_subsection, .popover--account-popover .popover .popover_menu_item .popover_menu_item_anchor, .popover_menu_item a, .post_brick .post_body p, .discover .discover-posts, .post_brick.is_quote .post_title, .blog-activity .ui_notes .blog-activity-date .date_header, .ui_notes .ui_note .part_main .activity {
    color: var(--first-color) !important;
  }  
  
  .post_full .post_header .post_info .post_info_link, .reblog-list-item .reblog-tumblelog-name, .post_brick .post-brick-source-footer, .name, .chrome.clear, .ui_notes_switcher .active, #range_controls h2.heading.arrow {
    color: var(--second-color) !important;
  }  
  
  a, .tab_bar .tab_anchor, .ui_stats .row .unit .brag {
   color: var(--third-color) !important; 
  }  
  
  
                                                  /* misc */
  
             /* shadows */
.post.post_full, .plus-follow-button, .l-header-container--refresh, .blog-sub-nav-details .blog-sub-nav-item.currentItem, .blog-sub-nav-details .blog-sub-nav-item.selected, .blog-sub-nav-details .blog-sub-nav-item:hover {
     box-shadow: 1px 2px 8px 3px rgba(0,0,0,0.2) !important;
  }  
  
  
           /* borders */
  
.post_brick .post-brick-source-footer, .reblog-list, .reblog-trail--original-post, .popover_header, .post_brick.is_conversation .post_header, .post_brick.is_note .post_header, .post_brick.is_quote .post_header, .post_brick.is_regular .post_header, .ui_notes .date_header, #tabs .tab, #tabs, input.text_field, .chrome.clear, .grey, .notifications_page .notifications_block, div.ui_note, .ui_stats .unit .button {
    border: none !important;
  }  
  
  .identity .controls_section.follow_list.recommended_tumblelogs > li.item, .reblog-list-item, .tab-bar-container .tab_notice, .post_full.is_note .post_body .note_item, .chrome.blue, .chrome_nav .chrome.sidebar {
    border-color: var(--bg-dark) !important;
  }  
  
.post_full .post_permalink {
    border-color: var(--bg) var(--bg) transparent transparent !important;
  }  
  
  
/* remove shit */  
  .no_push, .radar_header, .identity .controls_section.user_list li .follow_list_item_blog::before, .radar .radar_footer .radar_avatar::before, .identity .right_column::after, .reblog_follow_button, .post_full .post_tags::after, .post_full.is_note .nipple, .post-composer_note-post .nipple, .reblog_follow_button .follow-text, .worded-follow-button .follow-text, .post_brick .post_tags::after, .ui_stats .plop {
    display: none !important;
  }  
  
  .notifications_page .section.last {
    margin-top: 0px !important;
  }  
  
  .ui_stats .unit .button, .ui_stats .unit .stage {
    background: var(--bg2) !important;
  }  
  
/* fast compose bg */
#glass_overlay.blue, #hello_glass.blue, .blue.glass, .rapid-recs-container .rapid-recs {
 background: var(--bg3) !important;
}
  
  
                                /* remove things */
  
/* remove radar */
#tumblr_radar { display: none !important; }

/* remove dashboard compose section */
.post.new_post .post_buttons, .post.new_post { display: none !important; }  
  
  
   /* fade footer */
.pinned_sidebar_footer {
    opacity: 0 !important;
    transition: ease-in-out .3s !important;
    width: 1500px !important;
  }  
.pinned_sidebar_footer:hover {
    opacity: 0.8 !important;
    transition: ease-in-out .3s !important;
  }    
  
  
  
  
  
  
  
  
  
  
}
