style.css:
@-moz-document url-prefix('http://www.twitch.tv/') {
.message, .title, .channel-stats > span, .twitch_subwindow * {
    color: white !important;
}
.from {
    text-shadow: 1px 1px 6px #444 !important;
}

html, body, .tse-content, .panel.masonry-brick {
    background-color: black !important;
    color: white !important;
}
.ember-chat, .chat-room, .chat-header, .top, .chat-messages > div > .tse-content, .twitch_subwindow {
    background-color: #252525 !important;
    color: white !important;
}
.dropmenu_links_container, .dropmenu {
    background-color: #151518 !important;
}
ul.tabs li a {
    color: #99a !important;
}
ul.tabs li a:hover, ul.tabs li.selected a {
    color: #eef !important;
}

textarea, input {
    -moz-appearance: none !important;
    background-color: #101010 !important;
    color: white !important;
    border-color: #555 !important;
}
}
