style.css:
@-moz-document domain("slashdot.org") {

/* Hide all the crappy things!! */
a.comment_share_toggle,
div.col_1, 
nav.grid_14, 
div.synd, 
div.block.nosort, 
span.slant,
#slashboxes,
article > aside, 
article.fhitem-poll, 
article#firehose-000,
.details .story-views,
.topic,
article + iframe,
.janrainSocialPlaceholder,
.ntv-sponsored {
  display: none !important;
}

#firehose article header .details .story-details {
  border: 0;
  padding: 0;
}

h2.story {
  font-size:  /*[[textSize]]*/ !important;
  padding: 10px !important;
  line-height: inherit !important;
}

body {
  font-size:  /*[[textSize]]*/ !important;
}

header.h {
  max-width: 95%;
  position: absolute !important;
}

div.col_2 {
  margin: 55px 0px 0px 0px;
  min-width: initial;
}

#comments {
  margin-left: 10px;
}

#content {
  min-width: inherit;
}

.main-wrap.has-rail-right > .main-content {
  margin-right: 0px;
}

/** Rules for the header/comment control box **/
.ccw-count-container {
  width: 100%;
}

ul.besttabslist l.active {
  height: auto;
}

#ccw-abbr-count-text,
#ccw-hide-count-text,
#ccw-full-count-text {
  text-align: left !important;
}

/** Kill most border radius, seriously they're pointless **/
#firehose article,
#editor.slashdotthis header > h2.firehose, 
body.slashdot-it li.comment.full .title h4, 
header.h hgroup, 
#firehose article header h2, 
#comments h4, 
#modal_box #editor h2.firehose, 
.pollBooth_view .title h3, 
.bar .barColor, 
#d2header > div > nav, 
footer.nf {
  border-radius: 0px !important;
}

#comments .contain .comment {
  left: auto;
  padding-bottom: 10px;
}

.comment-bubble::before {
  content: " [";
}

.comment-bubble::after {
  position: relative !important;
  width: auto !important;
  height: auto !important;
  content: "]" !important;
  bottom: auto !important;
  left: auto  !important;
  border-width: 0 !important;
}

.comment-bubble {
  position: relative;
  top: 0;
  right: 0;
  background-color: transparent;
  width: auto !important;
  height: auto  !important;
  border-radius: 0  !important;
  text-align: left  !important;
  padding-left: 10px;
  padding-right: 10px;
}

#comments ul ul, 
#comments ul li ul li {
  margin: 5px 5px !important;
}

#firehose {
  margin-left: 0;
  margin-right: 0;
}

#firehose article {
  border: 1px solid #C8C8C8;
}

.cw {
  left: auto;
}

ul[id^="commtree"] {
  padding-right: 10px;
}

#firehose article footer {
  padding: 0 20px;
}

.commentSub a,
.commentSub div select,
.commentSub {
  font-size: 1em !important;
}

} /* /@-moz-document domain("slashdot.org") */
