}

options.value:
/* Fixed Header */
#header {
	position: fixed;
	width: 100%;
	height: 40px;
	box-sizing: border-box;
	top: 0;
	z-index: 1000;
	padding: 8px 4px;
	border-bottom-width: 3px;
}

#wrapper {
	padding-top: 40px;
}

#wrap_content {
	background-position: right 40px;
}

#navBar .icon {
	height: 50px;
	padding-top: 1px;
}

/* Smaller avatars in friend grid */
#friends_block .gridded_list li,
#userSocial_block .gridded_list li {
	height: 168px;
	width: 15.65%;
	clear: none !important;
}

#friends_block .gridded_list li img,
#userSocial_block .gridded_list li img {
	max-height:128px;
}

/* Limit feed content height */
#long_feeds .message.box > p,
#long_feeds .message.box [data-showmore] > p {
    max-height: 310px;
    overflow-y: auto;
    overflow-x: hidden;
}

/* More readable list updates */
[href^="https://naruho.do/anime/"]:before,
[href^="https://naruho.do/manga/"]:before {
    content: '\A• ';
}

[href^="https://naruho.do/anime/"],
[href^="https://naruho.do/manga/"] {
    white-space: pre;
}

/* Bigger rating text */
.comment_wrapper .vote a {
	margin: -0.5px 1px;
}
.comment_wrapper .vote a:first-child  {
	margin-top: -1px;
}
.comment_wrapper .vote a:last-child  {
	margin-bottom: -1px;
}

.comment_wrapper .vote span {
	display: block;
	font-size: 14px;
	margin-bottom: 2px;
}

/* Hide vote buttons unless hovered */
.comment_wrapper .vote:not(:hover) a:not(.active) {
	visibility: hidden;
}

/* Smaller anime/manga list rows */
#user_seriesList .cel.ds,
#user_seriesList .cel.position {
	padding:4px 0 !important;
}
#user_seriesList .cel.ds {
	padding-left:28px !important;
}
#list li .actions {
	top:5px;
}

/* Truncate sidebar text */
#feeds-rightBlock .hashtag a {
	white-space: nowrap;
	overflow: hidden;
	text-overflow: ellipsis;
}

body {
font-size: 12px;
	line-height: 16px;
}

.comment_wrapper .add-comment textarea {
    height: 22px;
    padding: 3px;
    padding-left: 26px;
}

.comment_wrapper .message.box {
    padding-top: 3px;
}

.comment_wrapper .message strong.user {
    margin-bottom: -2px;
    display: inline-block;
}

.comment_wrapper .message p {
    overflow: hidden;
    text-overflow: ellipsis;
    word-break: normal;
    word-break: break-word;
    margin: 0 !important;
}

.comment_wrapper .vote {
    width: 20px;
}

.date, .date a {
    font-size: 11px !important;
}

.comments .date {
    padding-left: 3px !important;
}

.date img {
    padding-left: 3px;
}

.comments .date img {
    padding-left: 0;
    margin-right: 3px !important;
}

.avatar.rounded img {
    max-height: 125px;
}

.comments .avatar {
    width: 36px;
}

.comments .avatar img {
    width: 36px !important;
}

.box, .comment_wrapper .add-comment {
    padding: 6px;
}

.comment_wrapper .add-comment {
    padding-top: 0px;
}

