}

/* Misc */
#navBar .icon {
    height: 47px;
}
#navBar #user_actions .username {
    height: 40px;
    margin-top: -15px;
}

.mt_videoSpoiler,
#long_feeds.particular .mt_videoSpoiler {
    padding: 1px 4px 2px !important;
