style.css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikia.com") {
    /*
        @JohnEdwa
        johnedwa.dy.fi
        johnedwa.deviantart.com
    
    	20151109 update
    */
    /* Disable some ads */
    
    #WikiaTopAds, #WikiaArticleBottomAd, #TOP_RIGHT_BOXAD, #LEFT_SKYSCRAPER_2, #ad-skin, .pagefair-acceptable, .wikia-ad, #jpsuperheader, #jpcenter, #jpright, #jpleft {
        display: none !important;
    }
    /* Hide some other stuffs */
    
    #WikiaBar,
    #WikiaRail,
    #videosModule,
    .start-a-wiki,
    #GlobalNavigation {
        display: none !important
    }
    /* Full width main content and some fixes */
    
    #WikiaMainContent,
    .WikiaMainContent {
        width: 100%;
    }
    .WikiaPageContentWrapper {
        margin: 10px;
    }
    /* Workarounds */
    /* Clear float of the talk page Add Topic button*/
    
    nav.wikia-menu-button:nth-child(2) {
        float: none !important;
    }
}
