style.css:
/*AGENT_SHEET*/ 
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url("chrome://browser/content/browser.xul") {    
/*_________________________________________________________
 |_|_|_________________________________________________|_|_| 
 |_|_|_________________________________________________|_|_|
 | | |                                                 | | |
 | | |  FIREFOX CUSTOMIZING & PANELUI THEME by nonaz   | | |
 | | |   (formerly 'Firefox Australis Menu Reskin')    | | |
 |_|_|_________________________________________________|_|_|
 |_|_|_________________________________________________|_|_|
 |_|_|_________________________________________________|_|_|    
  _________________________________________________________    
 |_|                                                     |_|
 |  Configurable settings are between lines 80 - 220 ota   |
 |  All box size options are included with each install    |
 |  just reordered, but if too confusing, the only setting | 
 |_ you cant change at userstyles is finetune width (105) _|
 |_|_____________________________________________________|_|*/
    
/* Darker grey background */    
window[customize-entered="true"]:-moz-lwtheme  {
    background: url("data:image/png;base64,") fixed,-moz-linear-gradient(to bottom,rgb(150,150,150),rgb(140,140,140)) !important;
    }
/* Lighter grey palette & panel backgrounds */    
window[customize-entered="true"] #customization-palette,
window[customize-entered="true"] #customization-panel-container .panel-arrowcontent { 
    border: 1px solid !important; border-color: rgba(200,200,200,.6) rgba(240,240,240,.8) rgba(240,240,240,.8) rgba(200,200,200,.6) !important;
    box-shadow: 1px 1px 2px rgba(40,40,40,.6) inset, -1px -1px 2px rgba(40,40,40,.5) inset !important;
    } 
/* Button box style */    
window[customize-entered="true"] #customization-palette > toolbarpaletteitem,
window[customize-entered="true"] toolbarpaletteitem[place="panel"]:not(:-moz-any(#wrapper-zoom-controls,#wrapper-edit-controls)) {
    border: 1px solid; border-color: rgba(255,255,255,.4) rgba(255,255,255,.5) rgba(255,255,255,.5) rgba(255,255,255,.4);
    box-shadow: 1px 1px 2px rgba(20,20,20,.25) inset,-1px -1px 1px rgba(20,20,20,.2) inset ;
    }      
window[customize-entered="true"]:-moz-lwtheme  #customization-palette > toolbarpaletteitem:hover:not(:active),
window[customize-entered="true"] toolbarpaletteitem[place="panel"]:not(:-moz-any(#wrapper-zoom-controls,#wrapper-edit-controls)):hover:not(:active) {
    background-color: rgba(0,0,0,.05) !important;
    border: 1px solid; border-color: rgba(255,255,255,.55) rgba(255,255,255,.65) rgba(255,255,255,.65) rgba(255,255,255,.55);
    box-shadow: 1px 1px 2px rgba(20,20,20,.2) inset, -1px -1px 2px rgba(20,20,20,.1) inset   
    }
window[customize-entered="true"] :-moz-any(#customization-footer,#customization-header,#customization-container,#customization-panel-container,#ctraddon_cuibuttonsbox) {
    background: transparent!important;
    border-color: transparent !important;
    } 
/* Dashed outline around navbar */    
window[customize-entered="true"] .customization-target:not(:-moz-any(#PanelUI-contents,#TabsToolbar,#toolbar-menubar))::before {
    outline-color: rgb(50,50,50) !important;
    }
window[customize-entered="true"] #bookmarks-toolbar-placeholder > label {
    text-shadow: none !important;
    color: rgb(60,60,60) !important;
    }     
/* Improves size & padding of left palette, the margin bottom values what lets it fit more in the container */    
window[customize-entered="true"] #customization-palette {
    border-radius: 4px !important;
    padding: 8px 0px 0 4px !important;
    margin: -30px -6px 0px 0 !important;
    } 
/* Moves the right panel 30px closer to the right window edge so same distance as left window edge - basic symmetry */    
window[customize-entered="true"] #customization-panel-container {
    margin-right: -30px !important;             
    }
/* The header label 'Additional Tools & Features' messes with the layout when hidden, 0% fonts the hack to remove it safely */    
window[customize-entered="true"] #customization-header { 
    font-size: 0% !important;
    }     
window[customize-entered="true"] :-moz-any(.panel-arrow,.panel-customization-placeholder,#PanelUI-help,#PanelUI-quit,#customization-empty,#PanelUI-fxa-status),    
window[customize-entered="true"] #PanelUI-customize :-moz-any(.toolbarbutton-text,.toolbarbutton-icon) {
    display: none !important }     
/*___________________________________________
 |                                           |
 | Customize window Icon size - 1 means 100% |
 |___________________________________________| also includes couple qualifiers for Hide Captions Titlebar Plus & Classic Theme Restorer buttons */
    
window[customize-entered="true"] #customization-panel-container toolbarpaletteitem .toolbarbutton-icon,    
window[customize-entered="true"] #customization-palette toolbarbutton:not(.hc-mmcbuttons-cls),
window[customize-entered="true"] #customization-palette :-moz-any(#ctraddon_window-controls,#hcp-mmc-buttons-item) {
    transform: scale(/*[[icon_custom]]*/) !important }
/*________________________________________________
 |                                                |
 | PanelUI popup Max height/Sync button/Icon size |
 |________________________________________________|*/
    
#PanelUI-popup #PanelUI-multiView { 
    max-height: /*[[max_panelui]]*/ !important } 
 
/*[[sync_panelui]]*/
    
#PanelUI-popup #PanelUI-contents toolbarbutton .toolbarbutton-icon {
    transform: scale(/*[[icon_panelui]]*/) !important }
/*________________________________________________
 |                                                |
 |  Themes popup Max height - more relevant with  | 
 |  low res screens, recommend 500px is necessary |
 |________________________________________________|*/   
    
 window[customize-entered="true"] #customization-lwtheme-menu  {
    max-height: /*[[max_theme]]*/ }                
/*____________________________________________________
  __|                                              |__
  __| FINETUNE BOX WIDTH BETWEEN 9.5 TO 10.5em     |__
  __| boxes only remain at preset width with these |__
  __| qualifiers set to 10 & adding/subtracting    |__
  __| .1em roughly adds/subtracts 1px to 1.5px     |__ 
  __|______________________________________________|__*/
    
/* ::: PanelUI popup ::: */    
#PanelUI-popup :-moz-any(.toolbarbutton-1.chromeclass-toolbar-additional,.toolbarbutton-1)   { 
    width: 10em !important }
    
/* ::: Customize window right side ::: */
window[customize-entered="true"] #customization-panel-container :-moz-any(.toolbarbutton-1.chromeclass-toolbar-additional,.toolbarbutton-1) { 
    width: 10em !important }
    
/* ::: Customize window left side ::: */
window[customize-entered="true"] #customization-palette toolbarpaletteitem {
    width: 10em !important } 
/*______________________________________________________________    
 |_|__________________________________________________________|_|
 | |                                                          | |
 | |  BOX PRESETS FOR PANELUI & CUSTOMIZE WINDOW (right side) | |
 |_|__________________________________________________________|_|
 |_|__________________________________________________________|_|*/      

/* ::: Reorder the one you want to load last ::: */
/*[[box_panelui]]*/

/* ::: Reorder the one you want to load last ::: */    
/*[[box_right]]*/     

#PanelUI-popup #PanelUI-contents toolbarbutton:not(.panel-combined-button) :-moz-any(.toolbarbutton-text,.toolbarbutton-multiline-text)  {
    text-shadow: 0px 1px 1px rgba(0,0,0,.15),0px -1px rgba(250,250,250,.4) !important;
    margin:  -2px -.2em 0em -.2em !important;
    color: rgb(65,65,65) !important;
    padding: 0 2px !important }   
#PanelUI-popup :-moz-any(.toolbarbutton-1.chromeclass-toolbar-additional,.toolbarbutton-1)   { 
    margin: .2em .1em .2em .3em !important;
    padding: 0em !important }  
    
window[customize-entered="true"] toolbarpaletteitem[place="panel"]:-moz-any(#wrapper-zoom-controls,#wrapper-edit-controls) {
    margin-top: -5px !important } 
window[customize-entered="true"] #customization-panel-container toolbarbutton:not(.panel-combined-button) label {
    text-shadow: 0px 1px 1px rgba(0,0,0,.15),0px -1px rgba(250,250,250,.3) !important;
    margin:  -2px -.2em 0em -.2em !important;
    color: rgb(80,80,80) !important;
    padding: 0 2px !important }  
window[customize-entered="true"] #customization-panel-container :-moz-any(.toolbarbutton-1.chromeclass-toolbar-additional,.toolbarbutton-1) { 
    margin: 0.1em !important;
    padding: 0em !important }  
window[customize-entered="true"] #PanelUI-contents {
    margin-left: 8px !important;} 
/*____________________________________________________    
 |_|________________________________________________|_|
 | |                                                | |
 | |  BOX PRESETS FOR CUSTOMIZE WINDOW (left side)  | |
 |_|________________________________________________|_|
 |_|________________________________________________|_|*/       

/* ::: Reorder the one you want to load last ::: */    
/*[[box_left]]*/    
    
window[customize-entered="true"] #customization-palette  toolbarpaletteitem > label {
    text-shadow: 0px 1px 1px rgba(0,0,0,.3) !important;
    color: rgb(80,80,80) !important;
    margin-top: -6px !important;  
    padding: 0 2px }
window[customize-entered="true"] #customization-palette toolbarpaletteitem {
    margin: 0px 2px !important; /* better with vsmall */
    margin: 1px 3px !important }   
/*___________________________
 |                           |
 | LEFT SIDE PALETTE BUTTONS |
 |___________________________|*/
    
/* Stops the buttons magnifying when grabbed/dragged - covers both left palette & right panel */    
window[customize-entered="true"] toolbarpaletteitem[mousedown] > :-moz-any(toolbaritem.panel-wide-item,toolbarbutton[type="menu-button"]),
window[customize-entered="true"] toolbarpaletteitem[mousedown] > toolbarbutton > .toolbarbutton-badge-stack > .toolbarbutton-icon,
window[customize-entered="true"] toolbarpaletteitem[mousedown] > toolbarbutton > .toolbarbutton-icon {
    transform: scale(1) !important 
    } 
window[customize-entered="true"] #customization-palette toolbarpaletteitem[mousedown] > label {
    margin-top: -8px !important 
    }
window[customize-entered="true"] #customization-palette toolbarpaletteitem[mousedown] {
    padding-bottom: 1px !important;
    padding-left: 1px !important; 
    } 
window[customize-entered="true"] #customization-palette > toolbarpaletteitem:hover:active {
    border: none !important;
    box-shadow: none !important;
    } 
window[customize-entered="true"] #customization-panel-container toolbarpaletteitem:hover:active {
    border: 1px solid transparent !important;
    box-shadow: none !important 
    }  
window[customize-entered="true"] #customization-palette #PanelUI-menu-button > stack {
    opacity: 0 !important;
    } 
window[customize-entered="true"] #PanelUI-contents #PanelUI-menu-button > stack {
    opacity: .7 ; margin:  .5em -.2em 1.1em -.2em !important;
    }  
window[customize-entered="true"] #customization-palette #ctraddon_statusbar {
    margin: 8px 0 !important;
    }   
window[customize-entered="true"] #customization-palette #wrapper-spring #spring {
    opacity: .8 ; margin: 8px 4px 12px 4px !important;
    border-color: rgba(0,0,0,.3) !important;
    }    
window[customize-entered="true"] #customization-palette #wrapper-spacer #spacer {
    opacity: .8 ; margin: 4px 0 !important;
    border-color: rgba(0,0,0,.4) !important;
    }    
window[customize-entered="true"] #customization-palette #wrapper-separator #separator {    
    margin: 4px 0 !important;
    }     
window[customize-entered="true"] #customization-palette :-moz-any(#hctp_fx_app_button_item,#hcp-web-title-item1,#hcp-web-title-item2) {
    margin-top: 10px !important;
    }     
window[customize-entered="true"] :-moz-any( #cut-button, #copy-button, #paste-button,#zoom-reset-button) {
    color:rgb(60,60,60) !important;
    text-shadow: 0px 1px 1px rgba(0,0,0,.3) !important;
    } 
window[customize-entered="true"] toolbarpaletteitem[place="panel"]:-moz-any(#wrapper-zoom-controls,#wrapper-edit-controls) {
    font: normal 100% verdana !important }
/*___________________________________________________________
 |                                                           |
 | RIGHT SIDE PANEL - new cutomize label & exit button label |
 |___________________________________________________________|*/    
    
window[customize-entered="true"] #PanelUI-contents:before {
    content: 'Customize' !important;
    font: small-caps bold 110% "dejavu serif",verdana !important;
    color: rgba(240,240,240,.75) !important;
    text-shadow: 0px 1px 1px rgba(0,0,0,.2),0px 1px 0px rgba(0,0,0,.5),0px -1px 0px rgba(255,255,255,.2) !important; 
    margin: 0 7em !important; 
    } 
window[customize-entered="true"] #PanelUI-customize:after {
    content: 'OK' !important;
    font:  bold 14px tahoma,"segoe ui","lucida console" !important;    
    color: rgb(80,80,80) !important;
    }
window[customize-entered="true"] #PanelUI-footer-fxa {
    border: none !important;
    }    
window[customize-entered="true"] #PanelUI-customize  {
    border-top: none !important; 
    border-color: transparent !important;
    outline-color: transparent !important;
    background: rgba(0,0,0,.08)!important;
    }    
window[customize-entered="true"] #PanelUI-customize:hover {    
    background-color: #6BCE59 !important;
    box-shadow: inset 0px -1px 2px rgba(0,0,0,.3), inset 1px 0px 2px rgba(0,0,0,.3)!important }        
/*____________________________________________
 |                                            | 
 | BOTTOM SECTION - BUTTON POSITIONS & RESKIN |   
 |____________________________________________|*/    
 
window[customize-entered="true"] #browser-bottombox {
    border: none !important;
    box-shadow: 1px 1px 0px rgb(220,220,220),-1px -1px 0px rgb(85,85,85),inset 1px 1px 1px rgba(0,0,0,.5),inset -1px -1px 1px rgba(0,0,0,.5) !important;
    }         
window[customize-entered="true"] :-moz-any(#customization-titlebar-visibility-button, #customization-toolbar-visibility-button,#customization-reset-button,#ctraddon_cui-addtoolbars,#ctraddon_cui-ctroldsearch,#ctraddon_cui-tabsnontop)  {
    margin-right: 0px !important;
