    margin: 5px 0px 0 2px !important;
    }   
window[customize-entered="true"] #ctraddon_cuibuttonsbox button[label="Mode"] {
    margin: 5px 0px 0 1px !important;
    }
window[customize-entered="true"] #ctraddon_cuibuttonsbox button[label="Buttons"] > hbox,
window[customize-entered="true"] #ctraddon_cuibuttonsbox button[label="Mode"] > hbox {
    border: 0 !important;
    } 
window[customize-entered="true"] #customization-footer button[label="Themes"] { 
    margin-right: -4px !important;
    }     
window[customize-entered="true"] :-moz-any(#customization-footer, #ctraddon_cuibuttonsbox) { 
    margin: 0 -15px 15px -15px !important;
    }     
window[customize-entered="true"] #ctraddon_cuibuttonsbox {
    margin-top: -25px !important; 
    }     
window[customize-entered="true"] #customization-footer .button-box {
    padding: 0px 2px !important;
    margin: -2px 0 !important; 
    } 
window[customize-entered="true"] #customization-titlebar-visibility-button .button-text {
    margin: -1px 2px 0px 2px !important;    
    }    
window[customize-entered="true"] #ctraddon_cuibuttonsbox button {
    height: 24px !important;
    margin-bottom: 0px !important;        
    }  
window[customize-entered="true"] #customization-footer button  {
    margin-top: 0px !important; 
    }    
window[customize-entered="true"] :-moz-any(#customization-titlebar-visibility-button,#ctraddon_cui-smallnavbut-button1,#ctraddon_cui-smallnavbut-button2,#ctraddon_cui-icons-button,#ctraddon_cui-iconstext-button,#ctraddon_cui-textonly-button,#ctraddon_cui-ctroldsearch,#ctraddon_cui-tabsnontop) {  
