window[customize-entered="true"] :-moz-any(#customization-titlebar-visibility-button,#ctraddon_cui-smallnavbut-button1,#ctraddon_cui-smallnavbut-button2,#ctraddon_cui-icons-button,#ctraddon_cui-iconstext-button,#ctraddon_cui-textonly-button,#ctraddon_cui-ctroldsearch,#ctraddon_cui-tabsnontop):hover .box-inherit { 
