    border: none !important; 
    margin: -1px !important;
    }
window[customize-entered="true"] #customization-titlebar-visibility-button .button-text { 
    text-shadow: none
    }
window[customize-entered="true"] :-moz-any(#ctraddon_cui-smallnavbut-button1,#ctraddon_cui-smallnavbut-button2,#ctraddon_cui-icons-button,#ctraddon_cui-iconstext-button,#ctraddon_cui-textonly-button,#ctraddon_cui-ctroldsearch,#ctraddon_cui-tabsnontop):hover { 
