    }     
window[customize-entered="true"] :-moz-any(#customization-titlebar-visibility-button,#ctraddon_cui-smallnavbut-button1,#ctraddon_cui-smallnavbut-button2,#ctraddon_cui-icons-button,#ctraddon_cui-iconstext-button,#ctraddon_cui-textonly-button,#ctraddon_cui-ctroldsearch,#ctraddon_cui-tabsnontop) .box-inherit { 
    background:rgba(0,0,0,.2) !important;
    background: -moz-linear-gradient(to bottom, rgba(170,170,170,.3), rgba(100,100,100,.4), rgba(0,0,0,.3)) !important;
    border: 0px !important;
    }   
window[customize-entered="true"] :-moz-any(#customization-toolbar-visibility-button,#customization-lwtheme-button,#customization-undo-reset-button,#customization-reset-button,#ctraddon_cui-addtoolbars)  {    
