style.css:
@-moz-document url-prefix("http://www.quotev.com/") {
/*mainbar recolor*/
.mainbar {background-color: steelblue !important}

/*main-font change*/
body {font-size: 15px; font-family: "Comic Sans MS"}

/*chat- or post-input font change*/
textarea, select, input {font-size: 15px; color: #333; font-family: "Comic Sans MS"}

/*mainbar text-shadow*/
div.mainbar a, div.mainbar a:visited, div.mainbar a:link {text-shadow: 1px 1px 5px black;}

/*notify-icon shadow*/
#notif_i {background-image: url(http://goo.gl/UkJf5X) !important; margin-left:-3px; margin-top:-2px; width:18px; height:20px; vertical-align:middle;}

/*message-box close x fix*/
body > div.dlg_pop > a {margin-top: 0px !important}

/*message-box recolor*/
div.dlg_context div.title, div.dlg_pop div.title {background-color: steelblue}

/*button recolor*/
.qbutton, .qbutton:visited, .request_form_submit, .qbutton_small, .qbutton_small:visited, .qbutton_small:hover {background-color: steelblue !important; font-size: 14px}

/*button:hover recolor*/
.qbutton:hover {background-color: dodgerblue !important; font-size: 14px}

/*mainbar menu hover recolor*/
div.mainbar a:hover, div.mainbar a.selected:hover, div.mainbar a.hover {background: dodgerblue !important; color: #fff}
div.drop_links a, div.drop_links a:visited, div.drop_links a:link {background: steelblue; border-top: 1px solid grey}

/*post reply entry box*/
.cke_editable {font-size: 15px !important; font-family: Comic Sans MS !important}
}
