style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("login.yahoo.com") 
{

body { 
  background: url("https://i.imgur.com/wZls5AJ.jpga") !important; 
  background-attachment: fixed !important;
  background-size: 100% !important; 
}

div.help-links,  #leftContent, div#footer, div#yucs, span.mm-or, div.lgbx-signup, img, div#iframeHolder
  {
  display:none!important;
    }
   
  input#passwd, input#username
  {
    background-color: rgba(0,0,0,0.2) !important;
    color: #fff !important;
    border: none !important;
    font-size: x-large !important;
  }
  
  button.purple-bg
  {
    background-color: rgba(0,0,0,0.2) !important;
    color: #fff !important;
    border: none !important;
    font-size: x-large !important;
    margin-left: -10px !important; 
  }
  
  div.lgbx
  {
    background-color: rgba(0,0,0,0) !important;
    margin-left: -400px !important;
    padding-top: 200px !important;
    width: 450px !important;
  }
 
  div.top.mm-lgbx, div#idpBtns, div.idp-string, button.lgbx-btn
  {
    background: none !important;
    border: none !important;
  }
  
  a, span
  {
    color: #FFF !important;
  }
  
  div.yregertxt, strong
  {
    color: #000 !important;
  }
  
  div.idp-string
  {
    margin-top: -118px !important;
    margin-left: 190px !important;
  }
  
}
