style.css:
@-moz-document domain('forums.thesims.com') {
html, body {
min-height: 100%;
height: 100%;
background-image: url('http://lvlt.thesims3.com/content/global/images/backgroundImages/Background_DragonValley_ver902864.jpg') !important;
background-attachment: fixed;
background-color: rgb(0, 36, 73);
background-repeat: no-repeat;
background-position: 47% top !important;
    font-family: Arial,Helvetica,"Trebuchet MS",sans-serif;
    font-size: 14px;
}
    
.DataTableWrap thead td {
    font-weight: 600;
}
    
.root-category td {
    background-color: #CEDFE7;
    background-image: linear-gradient(rgba(0, 0, 0, 0) 5%, rgba(0, 0, 0, 0.1));        
}
    
h2 {
    font-size: 16px;
}
    
.Section-Discussion .PageTitle h1 {
    font-weight: 600;
    text-transform: none;
}

.Item .Title {
    font-size: 14px;
}

.Message {
    line-height: 1.3;
}

.footer {
    background-color: #CEDFE7;
    color: #606060;
}

}
