style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url(http://www.corriere.it) {
.topSh_mm_ap_sp1,
.nav_city_links,
.md_zp_04,
.ftr_bkwrapper,
.topbar,
#multibar,
.nav-adv,
.nav_cross_links,
.bk_nw_fn,
#mkt_halfpage,
#header_social,
#ads_widepushbar,
#ads_halfpage {
    display: none;
}

.sub-default {
    padding-top: 0 ;
    background-image: none;
}

body > .container {
    max-width: 1150px;
}

.md_ap_bg,
.md_ap_sp1 {
    border-top: solid 1px lightgray;
    text-align: left;
}

.nav-header {
    margin-left: auto;
    margin-right: auto;
    float: none;
}

.nav_homesezione {
    text-align: center;
}

#header .nav_homesezione ul.nav_links {
    float: none;
}

.bk_cop_005 {
    overflow: auto;
    height: auto;
}

.article-title {
    overflow: visible;
    height: auto;
}

.nav_links {
    text-align: center;
    padding-top: 0;
}

body > #header {
    width: auto;
}

.container {
    width: auto;
    text-align: center;
}

.articolo {
    float: none;
    margin: 0 auto;
    max-width: 42em;
    width: 100%;
}

.type-articolo .container .container-body-article .chapter .chapter-title,
.article-subtitle,
.type-articolo .container .container-body-article .chapter .chapter-paragraph {
    font-size: 1.25rem;
    line-height: 1.75rem;
}
}
