@-moz-document url-prefix("http://pr0gramm.com") {

	.comments-large-rectangle > a > img {
		width: 0px;
		display: none;
	}

	.comments-large-rectangle {
		height: 0px;
		width: 0px;
		right: 0px;
		top: 0px;
		position: relative;
		display: none;
	}

	.side-wide-skyscraper {
	  width: 0px;
	  height: 0px;
	  overflow: hidden;
	  position: fixed;
	  top: 0px;
	  bottom: 0px;
	  left: 0px;
	  right: -1120px;
	  margin: auto;
	  z-index: 0;
	  display: none;
	 }
}
