style.css:
/*
  syosetu.com - solarized dark
*/
@-moz-document regexp('^https?://(ncode|novel(18)?)\\.syosetu\\.com/.*'),
regexp('^https?://syosetu\\.org/novel/.+\\.html$') {
  body,
  input,
  .novel_hyouka,
  .recommend_novel,
  .contents2,
  #container,
  #novel_color,
  #menu_on,
  #hyoukalan,
  #novel_attention,
  #recommend,
  #footer,
  #header_main,
  #head_nav,
  #header_bar {
    background-color: #002b36 !important;
    color: #839496 !important;
    border-color: #586e75 !important; }

  #container,
  #entry_box {
    border-style: none !important; }

  input,
  .genre,
  #hyoukalan,
  #novel_attention,
  .link_back,
  #head_nav li {
    border-color: #586e75 !important; }

  .contents1,
  .nothing,
  .novelnavi a,
  #novel_header,
  .novel_bn,
  #header_main,
  #head_nav {
    background: none;
    border-color: #586e75 !important; }

  .novel_hyouka label {
    color: #b58900 !important; }

  .novel_title,
  .novel_subtitle,
  .novel_writername,
  .chapter_title,
  .genre,
  #warning {
    background-color: #073642 !important;
    color: #93a1a1 !important; }

  #pageBottom,
  #pageTop {
    display: none !important; }

  img {
    background-color: #ffffff !important; }

  a {
    color: #268bd2 !important; }
    a:visited {
      color: #6c71c4 !important; } }
