    margin-right: 0 !important;
    margin-left: 71px !important;
}

.company_header, .user_header, .content_left, .sidebar_right, .rotated_posts {
    z-index: 1 !important;
    position: relative !important;
}

div.footer_panel:before {
    display: block !important;
    content: '' !important;
    left: -25px !important;
    right: -24px !important;
    background: #111 !important;
    top: -1000px !important;
    bottom: 0 !important;
    position: absolute !important;
    z-index: 0 !important;
}

#footer {
    position: relative !important;
    z-index: 1 !important;
}

#navbar .nav_panel .logo {
    background-position: 0 0 !important;
    background-size: 71px 71px  !important;
    background-image: url(http://habrastorage.org/files/da0/a28/a54/da0a28a5472e4a6b9c3c1c207c32c37d.png)  !important;
}

#navbar .nav_panel .tab.open, #navbar .nav_panel .tab:hover, #navbar .nav_tabs_content.open .nav_tab {
