}

.buttons button:disabled, .buttons a.button:disabled, .buttons input:disabled, .buttons button:disabled:active, .buttons a.button:disabled:active, .buttons input:disabled:active {
    text-shadow: none !important;
}

body .buttons a.button:disabled:hover, .buttons input:disabled:hover {
    background: #fafcfa  !important;
}

body .buttons a.button:hover, .buttons button:hover, .buttons a.button:hover, .buttons input:hover {
    background: #666  !important;
}

.buttons input.green {
    padding-left: 15px !important;
    padding-right: 15px !important;
}

.html_format pre code {
    font-size: 12px !important;
    padding: 0.5em !important;
    display: block !important;
}

.html_format .spoiler .spoiler_text {
    background: #444 !important;
    border: none !important;
}

.comments_list .comment_item .info.is_author, .conversation_page .messages .message.my .info {
    background: #554 !important;
}

.editor .text-holder textarea, input[type="text"], .chzn-container-multi .chzn-choices, .chzn-container .chzn-drop {
    background: #444  !important;
    color: #fff  !important;
    border-color: #555  !important;
}

.chzn-container-multi .chzn-choices .search-choice, .chzn-container .chzn-results .highlighted {
    color: #000 !important;
}

.chzn-container .chzn-results .highlighted .subscribers {
    color: #000 !important;
}

form .item label {
    color: #838383 !important;
}

.blue_buttons_panel {
    background: #6C747A !important;
}

.sidebar_right .block>.line-r {
    background: #504E4E !important;
}

.sidebar_right .block>.line-r:after {
    border-color: #646464 !important;
}

.editor .text-holder, .conversations .conversation:hover, .conversations .conversation.new {
    background: #444 !important;
    border-color: #444 !important;
}

.editor .panel {
    background: #ccc !important;
}

.tracker_page table[class^=tracker] tr th {
    border-bottom-color: #777 !important;
}

.notes table tr td, .conversations .conversation, .tracker_page table[class^=tracker] tr td {
    border-bottom-color: #555 !important;
}

.tracker_page table.tracker_comments tr td.comment_count a {
    color: #CF5555 !important;
}

.tracker_page table[class^=tracker] tr.new {
    background: #444 !important;
}

.conversation_page .messages .message {
    border-top: 1px solid #555 !important;
}

.conversation_page > .title, .conversation_page .all_dialogs {
    color: #ccc !important;
}

.conversations .conversation:hover a.delete {
    -webkit-filter: invert(.7) !important;
    filter: invert(.7) !important;
}

.post .content .poll .poll_title {
    color: #999 !important;
}

/* embedded video */

iframe, object {
    transition: all 300ms !important;
    opacity: 0.6 !important;
}

iframe:hover, object:hover {
    opacity: 1 !important;
}

/* разметка кода */

.html_format code, .html_format pre code {
    color: #A9B7C6 !important;
    background: #2b2b2b !important;
}

.html_format code {
    display: inline-block !important;
    padding: 0 3px !important;
}

code .subst, code .title {
    font-weight: normal !important;
    color: #FFC66D !important;
}

code .comment, code .template_comment, code .javadoc, code .diff .header {
    color: gray !important;
}

code .annotation, code .decorator, code .preprocessor, code .doctype, code .pi, code .chunk, code .shebang, code .apache .cbracket {
    color: olive !important;
}

code .tag, code .tag .title {
    color: #E8BF6A !important;
}

code .pi code .id, code .attr_selector, code .pseudo, code .literal, code .keyword, code .hexcolor, code .css .function, code .ini .title, code .css .class, code .list .title, code .tex .command {
    color: #CC7833 !important;
}

code .attribute, code .rules .keyword, code .number, code .date, code .regexp, code .tex .special {
    color: #9876AA !important;
}

code .number, code .regexp {
    font-weight: normal !important;
}

code .string, code .value, code .filter .argument, code .css .function .params, code .apache .tag {
    color: #390 !important;
}

code .symbol {
    color: #6E9CBE !important;
}

code .ruby .symbol .string, code .ruby .symbol .keyword, code .ruby .symbol .keymethods {
    color: #A5C25C !important;
}

code .char, code .tex .formula {
    color: #968e5b
 !important;
}

code .phpdoc, code .yardoctag, code .javadoctag {
    text-decoration: underline
 !important;
}

code .variable, code .envvar, code .apache .sqbracket, code .nginx .built_in {
    color: #D0D0FF
 !important;
}

code .addition, code .deletion, code .diff .change {
    display: block !important;
    margin-bottom: -1.6em !important;
}

code .addition {
    background: #294436
 !important;
}

code .deletion {
    background: #484A4A
 !important;
}

code .diff .change {
    background: #385570
 !important;
}

